            # Alt+Up で親ディレクトリに移動
            function cd-up () {
              cd ..
              zle reset-prompt
            }
            zle -N cd-up
            bindkey '^[[1;3A' cd-up
    '';
    plugins = [
      {
        name = "fzf-tab";
        src = pkgs.zsh-fzf-tab;
      }
    ];
    oh-my-zsh = {
      enable = true;
      theme = "agnoster";
      plugins = [
        "git"
        "docker"
        "kubectl"
        "history"
        "sudo"
        "bgnotify"
      ];
    };
  };
}

