  1→{
  2→  description = "My package definition";
  3→
  4→  inputs = {
  5→    nixpkgs.url = "github:NixOS/nixpkgs/nixpkgs-unstable";
  6→    flake-compat = {
  7→      url = "github:edolstra/flake-compat";
  8→      flake = false;
  9→    };
 10→    git-hooks = {
 11→      url = "github:cachix/git-hooks.nix";
 12→      inputs.nixpkgs.follows = "nixpkgs";
 13→      inputs.flake-compat.follows = "flake-compat";
 14→    };
 15→    flake-parts = {
 16→      url = "github:hercules-ci/flake-parts";
 17→    };
 18→    treefmt-nix = {
 19→      url = "github:numtide/treefmt-nix";
 20→      inputs.nixpkgs.follows = "nixpkgs";
 21→    };
 22→    hercules-ci-effects = {
 23→      url = "github:hercules-ci/hercules-ci-effects";
 24→      inputs.nixpkgs.follows = "nixpkgs";
 25→      inputs.flake-parts.follows = "flake-parts";
 26→    };
 27→    neovim-nightly-overlay = {
 28→      url = "github:nix-community/neovim-nightly-overlay";
 29→      inputs.nixpkgs.follows = "nixpkgs";
 30→      inputs.flake-parts.follows = "flake-parts";
 31→    };
 32→    vim-overlay = {
 33→      url = "github:kawarimidoll/vim-overlay";
 34→      inputs.nixpkgs.follows = "nixpkgs";
 35→      inputs.pre-commit-hooks.follows = "git-hooks";
 36→    };
 37→    home-manager = {
 38→      url = "github:nix-community/home-manager";
 39→      inputs.nixpkgs.follows = "nixpkgs";
 40→    };
 41→    koi = {
 42→      url = "github:kawarimidoll/koi";
 43→      inputs.nixpkgs.follows = "nixpkgs";
 44→      inputs.git-hooks.follows = "git-hooks";
 45→    };
 46→    cage = {
 47→      url = "github:Warashi/cage";
 48→      inputs.nixpkgs.follows = "nixpkgs";
 49→      inputs.git-hooks.follows = "git-hooks";
 50→      inputs.flake-parts.follows = "flake-parts";
 51→      inputs.treefmt-nix.follows = "treefmt-nix";
 52→    };
 53→    gh-graph = {
 54→      url = "github:kawarimidoll/gh-graph";
 55→      inputs.nixpkgs.follows = "nixpkgs";
 56→      inputs.flake-parts.follows = "flake-parts";
 57→    };
 58→    gh-q = {
 59→      url = "github:kawarimidoll/gh-q";
 60→      inputs.nixpkgs.follows = "nixpkgs";
 61→      inputs.flake-parts.follows = "flake-parts";
 62→    };
 63→    nix-darwin = {
 64→      url = "github:nix-darwin/nix-darwin";
 65→      inputs.nixpkgs.follows = "nixpkgs";
 66→    };
 67→    nur-packages = {
 68→      url = "github:kawarimidoll/nur-packages";
 69→      inputs.nixpkgs.follows = "nixpkgs";
 70→    };
 71→    hjkls = {
 72→      url = "github:kawarimidoll/hjkls";
 73→      inputs.nixpkgs.follows = "nixpkgs";
 74→      inputs.git-hooks.follows = "git-hooks";
 75→    };
 76→    crane = {
 77→      url = "github:ipetkov/crane";
 78→    };
 79→    arto = {
 80→      url = "github:arto-app/Arto";
 81→      inputs.nixpkgs.follows = "nixpkgs";
 82→      inputs.crane.follows = "crane";
 83→    };
 84→    version-lsp = {
 85→      url = "github:skanehira/version-lsp";
 86→      inputs.nixpkgs.follows = "nixpkgs";
 87→      inputs.flake-parts.follows = "flake-parts";
 88→      inputs.crane.follows = "crane";
 89→    };
 90→    nix-index-database = {
 91→      url = "github:nix-community/nix-index-database";
 92→      inputs.nixpkgs.follows = "nixpkgs";
 93→    };
 94→    llm-agents = {
 95→      url = "github:numtide/llm-agents.nix";
 96→      inputs.nixpkgs.follows = "nixpkgs";
 97→      inputs.treefmt-nix.follows = "treefmt-nix";
 98→    };
 99→  };
100→
101→  outputs =
102→    {
103→      self,
104→      nixpkgs,
105→      home-manager,
106→      nix-darwin,
107→      ...
108→    }@inputs:
109→    let
110→      system = "aarch64-darwin";
111→      pkgs = import nixpkgs { inherit system; };
112→    in
113→    {
114→      apps.${system} = {
115→        update = {
116→          type = "app";
117→          program = toString (
118→            pkgs.writeShellScript "update-script" ''
119→              set -e
120→              echo "Updating flake..."
121→              nix flake update
122→              echo "Updating home-manager..."
123→              nix run nixpkgs#home-manager -- switch --flake .#myHomeConfig
124→              echo "Updating nix-darwin..."
125→              nix run nix-darwin -- switch --flake .#kawarimidoll-darwin
126→              echo "Update complete!"
127→            ''
128→          );
129→        };
130→
131→        update-home = {
132→          type = "app";
133→          program = toString (
134→            pkgs.writeShellScript "update-script" ''
135→              set -e
136→              echo "Updating flake..."
137→              nix flake update
138→              echo "Updating home-manager..."
139→              nix run nixpkgs#home-manager -- switch --flake .#myHomeConfig
140→              echo "Update complete!"
141→            ''
142→          );
143→        };
144→        update-darwin = {
145→          type = "app";
146→          program = toString (
147→            pkgs.writeShellScript "update-script" ''
148→              set -e
149→              echo "Updating flake..."
150→              nix flake update
151→              echo "Updating nix-darwin..."
152→              sudo darwin-rebuild switch --flake .#kawarimidoll-darwin
153→              echo "Update complete!"
154→            ''
155→          );
156→        };
157→      };
158→
159→      darwinConfigurations.kawarimidoll-darwin = nix-darwin.lib.darwinSystem {
160→        system = system;
161→        modules = [ ./nix/nix-darwin/default.nix ];
162→      };
163→
164→      homeConfigurations = {
165→        myHomeConfig = home-manager.lib.homeManagerConfiguration {
166→          pkgs = pkgs;
167→          extraSpecialArgs = {
168→            inherit inputs;
169→          };
170→          modules = [ ./nix/home-manager/default.nix ];
171→        };
172→      };
173→    };
174→}
175→# update:
176→#   nix run .#update (all)
177→#   nix run .#update-home
178→#   nix run .#update-darwin
