  1→{
  2→  description = "yuta's NixOS & macOS configuration";
  3→
  4→  inputs = {
  5→    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
  6→    nixpkgs-stable.url = "github:NixOS/nixpkgs/nixos-25.11";
  7→    flake-parts = {
  8→      url = "github:hercules-ci/flake-parts";
  9→      inputs.nixpkgs-lib.follows = "nixpkgs";
 10→    };
 11→    treefmt-nix = {
 12→      url = "github:numtide/treefmt-nix";
 13→      inputs.nixpkgs.follows = "nixpkgs";
 14→    };
 15→    ghostty.url = "github:ghostty-org/ghostty";
 16→    home-manager = {
 17→      url = "github:nix-community/home-manager";
 18→      inputs.nixpkgs.follows = "nixpkgs";
 19→    };
 20→    niri = {
 21→      url = "github:sodiboo/niri-flake";
 22→      inputs.nixpkgs.follows = "nixpkgs";
 23→    };
 24→    llm-agents.url = "github:numtide/llm-agents.nix";
 25→    nix-steipete-tools.url = "github:openclaw/nix-steipete-tools";
 26→    gh-nippou = {
 27→      url = "github:ryoppippi/gh-nippou";
 28→      inputs.nixpkgs.follows = "nixpkgs";
 29→    };
 30→    gh-graph = {
 31→      url = "github:kawarimidoll/gh-graph";
 32→      inputs.nixpkgs.follows = "nixpkgs";
 33→    };
 34→    nix-hazkey = {
 35→      url = "github:aster-void/nix-hazkey";
 36→      inputs.nixpkgs.follows = "nixpkgs";
 37→    };
 38→    version-lsp = {
 39→      url = "github:skanehira/version-lsp";
 40→      inputs.nixpkgs.follows = "nixpkgs";
 41→    };
 42→    agent-skills = {
 43→      url = "github:Kyure-A/agent-skills-nix";
 44→      inputs.nixpkgs.follows = "nixpkgs";
 45→    };
 46→    anthropic-skills = {
 47→      url = "github:anthropics/skills";
 48→      flake = false;
 49→    };
 50→    vercel-skills = {
 51→      url = "github:vercel-labs/skills";
 52→      flake = false;
 53→    };
 54→    ui-ux-pro-max-skill = {
 55→      url = "github:nextlevelbuilder/ui-ux-pro-max-skill";
 56→      flake = false;
 57→    };
 58→    ast-grep-skill = {
 59→      url = "github:ast-grep/claude-skill";
 60→      flake = false;
 61→    };
 62→    cloudflare-skills = {
 63→      url = "github:cloudflare/skills";
 64→      flake = false;
 65→    };
 66→    hashicorp-agent-skills = {
 67→      url = "github:hashicorp/agent-skills";
 68→      flake = false;
 69→    };
 70→    deno-skills = {
 71→      url = "github:denoland/skills";
 72→      flake = false;
 73→    };
 74→    aws-agent-skills = {
 75→      url = "github:itsmostafa/aws-agent-skills";
 76→      flake = false;
 77→    };
 78→    obsidian-skills = {
 79→      url = "github:kepano/obsidian-skills";
 80→      flake = false;
 81→    };
 82→    repiq = {
 83→      url = "github:yutakobayashidev/repiq";
 84→      inputs.nixpkgs.follows = "nixpkgs";
 85→    };
 86→    nix-darwin = {
 87→      url = "github:nix-darwin/nix-darwin";
 88→      inputs.nixpkgs.follows = "nixpkgs";
 89→    };
 90→    onepassword-shell-plugins.url = "github:1Password/shell-plugins";
 91→    brew-nix = {
 92→      url = "github:BatteredBunny/brew-nix";
 93→      inputs = {
 94→        brew-api.follows = "brew-api";
 95→        nix-darwin.follows = "nix-darwin";
 96→        nixpkgs.follows = "nixpkgs";
 97→      };
 98→    };
 99→    brew-api = {
100→      url = "github:BatteredBunny/brew-api";
101→      flake = false;
102→    };
103→    git-hooks = {
104→      url = "github:cachix/git-hooks.nix";
105→      inputs.nixpkgs.follows = "nixpkgs";
106→    };
107→    mcp-servers-nix = {
108→      url = "github:natsukium/mcp-servers-nix";
109→      inputs.nixpkgs.follows = "nixpkgs";
110→    };
111→    nix-filter.url = "github:numtide/nix-filter";
112→    # TODO: Pinned to specific nixpkgs commit as workaround for nix-community/nix-on-droid#495
113→    # Issue: "getting pseudoterminal attributes: Permission denied" with nixpkgs after 2026-01-24
114→    nix-on-droid = {
115→      url = "github:nix-community/nix-on-droid";
116→      inputs.nixpkgs.url = "github:NixOS/nixpkgs/2bceeb45e516fc6956714014c92ddfdafe4c9da3";
117→      inputs.home-manager.follows = "home-manager";
118→    };
119→    nix-index-database = {
120→      url = "github:nix-community/nix-index-database";
121→      inputs.nixpkgs.follows = "nixpkgs";
122→    };
123→    nixos-wsl = {
124→      url = "github:nix-community/NixOS-WSL";
125→      inputs.nixpkgs.follows = "nixpkgs";
126→    };
127→  };
128→
129→  nixConfig = {
130→    extra-substituters = [
131→      "https://cache.nixos.org"
132→      "https://cache.numtide.com"
133→      "https://yuta.cachix.org"
134→      "https://devenv.cachix.org"
135→      "https://nix-community.cachix.org"
136→    ];
137→    extra-trusted-public-keys = [
138→      "cache.nixos.org-1:REDACTED"
139→      "niks3.numtide.com-1:DTx8wZduET09hRmMtKdQDxNNthLQETkc/yaX7M4qK0g="
140→      "yuta.cachix.org-1:VGiC7m0kQjut7lp+RG/9pCRHFpzf11ELQrM2Nc2QCCk="
141→      "devenv.cachix.org-1:REDACTED/Hm9XgU50cw="
142→      "nix-community.cachix.org-1:REDACTED/rkCWyvRCYg3Fs="
143→    ];
144→  };
145→
146→  outputs =
147→    inputs@{
148→      flake-parts,
149→      treefmt-nix,
150→      nixpkgs,
151→      nixpkgs-stable,
152→      llm-agents,
153→      nix-steipete-tools,
154→      version-lsp,
155→      ghostty,
156→      gh-nippou,
157→      gh-graph,
158→      brew-nix,
159→      ...
160→    }:
161→    let
162→      mkPkgs =
163→        system:
164→        let
165→          isDarwin = builtins.match ".*-darwin" system != null;
166→        in
167→        import nixpkgs {
168→          inherit system;
169→          config = {
170→            allowUnfree = true;
171→            android_sdk.accept_license = true;
172→          };
173→          overlays = [
174→            (_final: _prev: {
175→              stable = import nixpkgs-stable {
176→                inherit system;
177→                config = {
178→                  allowUnfree = true;
179→                };
180→              };
181→            })
182→            (_final: _prev: {
183→              _llm-agents = llm-agents;
184→              _nix-steipete-tools = nix-steipete-tools;
185→              _version-lsp = version-lsp;
186→              _ghostty = ghostty;
187→              _repiq = inputs.repiq;
188→            })
189→            gh-nippou.overlays.default
190→            gh-graph.overlays.default
191→            (import ./nix/overlays/default.nix)
192→          ]
193→          ++ nixpkgs.lib.optionals isDarwin [
194→            brew-nix.overlays.default
195→          ];
196→        };
197→    in
198→    flake-parts.lib.mkFlake { inherit inputs; } {
199→      systems = [
200→        "x86_64-linux"
201→        "aarch64-darwin"
202→      ];
203→
204→      imports = [
205→        treefmt-nix.flakeModule
206→        inputs.git-hooks.flakeModule
207→      ];
208→
209→      perSystem =
210→        {
211→          pkgs,
212→          system,
213→          config,
214→          ...
215→        }:
216→        let
217→          isDarwin = builtins.match ".*-darwin" system != null;
218→          darwinHostname = "M2-MacBook-Air";
219→          nom = "${pkgs.nix-output-monitor}/bin/nom";
220→          allPkgs = mkPkgs system;
221→        in
222→        {
223→          packages = {
224→            inherit (allPkgs)
225→              difit
226→              jj-desc
227→              keifu
228→              polycat
229→              pretty-ts-errors-markdown
230→              similarity-ts
231→              ;
232→          };
233→          apps = {
234→            build = {
235→              type = "app";
236→              program = toString (
237→                pkgs.writeShellScript "build" ''
238→                  set -e
239→                  ${
240→                    if isDarwin then
241→                      ''
242→                        echo "Building darwin configuration..."
243→                        ${nom} build ".#darwinConfigurations.${darwinHostname}.system"
244→                      ''
245→                    else
246→                      ''
247→                        HOSTNAME="$(hostname)"
248→                        echo "Building NixOS configuration for $HOSTNAME..."
249→                        ${nom} build ".#nixosConfigurations.$HOSTNAME.config.system.build.toplevel"
250→                      ''
251→                  }
252→                  echo "Build successful! Run 'nix run .#switch' to apply."
253→                ''
254→              );
255→            };
256→            switch = {
257→              type = "app";
258→              program = toString (
259→                pkgs.writeShellScript "switch" ''
260→                  set -eo pipefail
261→                  ${
262→                    if isDarwin then
263→                      ''
264→                        echo "Switching to darwin configuration..."
265→                        darwin-rebuild switch --flake ".#${darwinHostname}" |& ${nom}
266→                      ''
267→                    else
268→                      ''
269→                        HOSTNAME="$(hostname)"
270→                        echo "Switching to NixOS configuration for $HOSTNAME..."
271→                        sudo nixos-rebuild switch --flake ".#$HOSTNAME" |& ${nom}
272→                      ''
273→                  }
274→                  echo "Done!"
275→                ''
276→              );
277→            };
278→            fmt = {
279→              type = "app";
