    pname = "tunnelto";
    version = "0.1.18";

    src = final.fetchFromGitHub {
      owner = "agrinman";
      repo = "tunnelto";
      rev = version;
      hash = "REDACTED";
    };

    cargoHash = "REDACTED/2qT5nzd5F2VNU=";

    nativeBuildInputs = [ final.pkg-config ];

    buildInputs = [ openssl_1_1' ];

    buildAndTestSubdir = "tunnelto";

