  rtkHookPath = "${homeDirectory}/.claude/hooks/rtk-rewrite.sh";
  terminal-notifier =
    if pkgs.stdenv.isDarwin then lib.getExe' pkgs.terminal-notifier "terminal-notifier" else "";

