      "types": "./dist/index.d.ts",
      "import": "./dist/index.js"
    },
    "./cli": "./dist/cli.js"
  },
  "bin": {
    "continues": "dist/cli.js",
    "cont": "dist/cli.js"
  },
  "type": "module",
