{
  description = "yuta's NixOS & macOS configuration";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    nixpkgs-stable.url = "github:NixOS/nixpkgs/nixos-25.11";
    flake-parts = {
      url = "github:hercules-ci/flake-parts";
      inputs.nixpkgs-lib.follows = "nixpkgs";
    };
    treefmt-nix = {
      url = "github:numtide/treefmt-nix";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    ghostty.url = "github:ghostty-org/ghostty";
    home-manager = {
      url = "github:nix-community/home-manager";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    niri = {
      url = "github:sodiboo/niri-flake";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    llm-agents.url = "github:numtide/llm-agents.nix";
    nix-steipete-tools.url = "github:openclaw/nix-steipete-tools";
    gh-nippou = {
      url = "github:ryoppippi/gh-nippou";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    gh-graph = {
      url = "github:kawarimidoll/gh-graph";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    nix-hazkey = {
      url = "github:aster-void/nix-hazkey";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    version-lsp = {
      url = "github:skanehira/version-lsp";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    agent-skills = {
      url = "github:Kyure-A/agent-skills-nix";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    anthropic-skills = {
      url = "github:anthropics/skills";
      flake = false;
    };
    vercel-skills = {
      url = "github:vercel-labs/skills";
      flake = false;
    };
    ui-ux-pro-max-skill = {
      url = "github:nextlevelbuilder/ui-ux-pro-max-skill";
      flake = false;
    };
    ast-grep-skill = {
      url = "github:ast-grep/claude-skill";
      flake = false;
    };
    cloudflare-skills = {
      url = "github:cloudflare/skills";
      flake = false;
    };
    hashicorp-agent-skills = {
      url = "github:hashicorp/agent-skills";
      flake = false;
    };
    deno-skills = {
      url = "github:denoland/skills";
      flake = false;
    };
    aws-agent-skills = {
      url = "github:itsmostafa/aws-agent-skills";
      flake = false;
    };
    obsidian-skills = {
      url = "github:kepano/obsidian-skills";
      flake = false;
    };
    nix-darwin = {
      url = "github:nix-darwin/nix-darwin";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    onepassword-shell-plugins.url = "github:1Password/shell-plugins";
    brew-nix = {
      url = "github:BatteredBunny/brew-nix";
      inputs = {
        brew-api.follows = "brew-api";
        nix-darwin.follows = "nix-darwin";
        nixpkgs.follows = "nixpkgs";
      };
    };
    brew-api = {
      url = "github:BatteredBunny/brew-api";
      flake = false;
    };
    git-hooks = {
      url = "github:cachix/git-hooks.nix";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    mcp-servers-nix = {
      url = "github:natsukium/mcp-servers-nix";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    moonbit-overlay.url = "github:moonbit-community/moonbit-overlay";
    nix-filter.url = "github:numtide/nix-filter";
    # TODO: Pinned to specific nixpkgs commit as workaround for nix-community/nix-on-droid#495
    # Issue: "getting pseudoterminal attributes: Permission denied" with nixpkgs after 2026-01-24
    nix-on-droid = {
      url = "github:nix-community/nix-on-droid";
      inputs.nixpkgs.url = "github:NixOS/nixpkgs/2bceeb45e516fc6956714014c92ddfdafe4c9da3";
      inputs.home-manager.follows = "home-manager";
    };
    nixos-wsl = {
      url = "github:nix-community/NixOS-WSL";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    ashiba = {
      url = "github:yutakobayashidev/ashiba";
      flake = false;
    };
    template-web-app = {
      url = "github:hiroppy/web-app-template";
      flake = false;
    };
  };

  nixConfig = {
    extra-substituters = [
      "https://cache.nixos.org"
      "https://cache.numtide.com"
      "https://yuta.cachix.org"
      "https://devenv.cachix.org"
      "https://nix-community.cachix.org"
    ];
    extra-trusted-public-keys = [
      "cache.nixos.org-1:REDACTED"
      "niks3.numtide.com-1:REDACTED"
      "yuta.cachix.org-1:REDACTED"
      "devenv.cachix.org-1:REDACTED"
      "nix-community.cachix.org-1:REDACTED"
    ];
  };

  outputs =
    inputs@{
      flake-parts,
      treefmt-nix,
      nixpkgs,
      nixpkgs-stable,
      llm-agents,
      nix-steipete-tools,
      version-lsp,
      ghostty,
      gh-nippou,
      gh-graph,
      moonbit-overlay,
      brew-nix,
      ...
    }:
    let
      mkPkgs =
        system:
        let
          isDarwin = builtins.match ".*-darwin" system != null;
        in
        import nixpkgs {
          inherit system;
          config = {
            allowUnfree = true;
            android_sdk.accept_license = true;
          };
          overlays = [
            (_final: _prev: {
              stable = import nixpkgs-stable {
                inherit system;
                config = {
                  allowUnfree = true;
                };
              };
            })
            (_final: _prev: {
              _llm-agents = llm-agents;
              _nix-steipete-tools = nix-steipete-tools;
              _version-lsp = version-lsp;
              _ghostty = ghostty;
            })
            gh-nippou.overlays.default
            gh-graph.overlays.default
            (import ./nix/overlays/default.nix)
          ]
          ++ nixpkgs.lib.optionals (!isDarwin) [
            moonbit-overlay.overlays.default
          ]
          ++ nixpkgs.lib.optionals isDarwin [
            brew-nix.overlays.default
          ];
        };
    in
    flake-parts.lib.mkFlake { inherit inputs; } {
      systems = [
        "x86_64-linux"
        "aarch64-darwin"
      ];

      imports = [
        treefmt-nix.flakeModule
        inputs.git-hooks.flakeModule
      ];

      perSystem =
        {
          pkgs,
          system,
          config,
          ...
        }:
        let
          isDarwin = builtins.match ".*-darwin" system != null;
          darwinHostname = "M2-MacBook-Air";
          nom = "${pkgs.nix-output-monitor}/bin/nom";
          allPkgs = mkPkgs system;
        in
        {
          packages = {
            inherit (allPkgs)
              difit
              jj-desc
              keifu
              polycat
              pretty-ts-errors-markdown
              similarity-ts
              ;
          };
          apps = {
            build = {
              type = "app";
              program = toString (
                pkgs.writeShellScript "build" ''
                  set -e
                  ${
                    if isDarwin then
                      ''
                        echo "Building darwin configuration..."
                        ${nom} build ".#darwinConfigurations.${darwinHostname}.system"
                      ''
                    else
                      ''
                        HOSTNAME="$(hostname)"
                        echo "Building NixOS configuration for $HOSTNAME..."
                        ${nom} build ".#nixosConfigurations.$HOSTNAME.config.system.build.toplevel"
                      ''
                  }
                  echo "Build successful! Run 'nix run .#switch' to apply."
                ''
              );
            };
            switch = {
              type = "app";
              program = toString (
                pkgs.writeShellScript "switch" ''
                  set -eo pipefail
                  ${
                    if isDarwin then
                      ''
                        echo "Switching to darwin configuration..."
                        darwin-rebuild switch --flake ".#${darwinHostname}" |& ${nom}
                      ''
                    else
                      ''
                        HOSTNAME="$(hostname)"
                        echo "Switching to NixOS configuration for $HOSTNAME..."
                        sudo nixos-rebuild switch --flake ".#$HOSTNAME" |& ${nom}
                      ''
                  }
                  echo "Done!"
                ''
              );
            };
            fmt = {
              type = "app";
              program = toString (
                pkgs.writeShellScript "treefmt-wrapper" ''
                  exec ${config.treefmt.build.wrapper}/bin/treefmt "$@"
                ''
              );
            };
          };

          treefmt = {
            projectRootFile = "flake.nix";
            programs = {
              nixfmt.enable = true;
              stylua.enable = true;
              shfmt.enable = true;
            };
            settings.global.excludes = [
              ".git/**"
              "*.lock"
            ];
          };

          pre-commit = {
            check.enable = false;
            settings.hooks = {
              treefmt = {
                enable = true;
                package = config.treefmt.build.wrapper;
              };
              git-secrets = {
                enable = true;
                entry = "${pkgs.git-secrets}/bin/git-secrets --pre_commit_hook";
                language = "system";
                stages = [ "pre-commit" ];
                excludes = [ "^\.direnv/" ];
              };
            };
          };

          devShells.default = pkgs.mkShell {
            shellHook = ''
              ${config.pre-commit.installationScript}
            '';
          };
        };

      flake = {
        nixosConfigurations = {
          UM790-Pro = import ./nix/hosts/UM790-Pro { inherit inputs mkPkgs; };
          Still-Legend-x870 = import ./nix/hosts/Still-Legend-x870 { inherit inputs mkPkgs; };
        };

        darwinConfigurations = {
          M2-MacBook-Air = import ./nix/hosts/M2-MacBook-Air { inherit inputs mkPkgs; };
        };

        nixOnDroidConfigurations = {
          Galaxy-S23FE = import ./nix/hosts/Galaxy-S23FE { inherit inputs; };
        };

        templates = import ./template.nix { inherit inputs; };
      };
    };
}

