 92→      url = "github:BatteredBunny/brew-nix";
 93→      inputs = {
 94→        brew-api.follows = "brew-api";
 95→        nix-darwin.follows = "nix-darwin";
 96→        nixpkgs.follows = "nixpkgs";
 97→      };
 98→    };
 99→    brew-api = {
100→      url = "github:BatteredBunny/brew-api";
101→      flake = false;
102→    };
103→    git-hooks = {
104→      url = "github:cachix/git-hooks.nix";
105→      inputs.nixpkgs.follows = "nixpkgs";
106→    };
107→    cage = {
108→      url = "github:Warashi/cage";
109→      inputs.nixpkgs.follows = "nixpkgs";
110→      inputs.git-hooks.follows = "git-hooks";
111→      inputs.flake-parts.follows = "flake-parts";
112→      inputs.treefmt-nix.follows = "treefmt-nix";
113→    };
114→    mcp-servers-nix = {
115→      url = "github:natsukium/mcp-servers-nix";
116→      inputs.nixpkgs.follows = "nixpkgs";
117→    };
118→    nix-filter.url = "github:numtide/nix-filter";
119→    # TODO: Pinned to specific nixpkgs commit as workaround for nix-community/nix-on-droid#495
120→    # Issue: "getting pseudoterminal attributes: Permission denied" with nixpkgs after 2026-01-24
121→    nix-on-droid = {
122→      url = "github:nix-community/nix-on-droid";
123→      inputs.nixpkgs.url = "github:NixOS/nixpkgs/2bceeb45e516fc6956714014c92ddfdafe4c9da3";
124→      inputs.home-manager.follows = "home-manager";
125→    };
126→    nix-index-database = {
127→      url = "github:nix-community/nix-index-database";
128→      inputs.nixpkgs.follows = "nixpkgs";
129→    };
130→    nixos-wsl = {
131→      url = "github:nix-community/NixOS-WSL";
132→      inputs.nixpkgs.follows = "nixpkgs";
133→    };
134→  };
135→
136→  nixConfig = {
137→    extra-substituters = [
138→      "https://cache.nixos.org"
139→      "https://cache.numtide.com"
140→      "https://yuta.cachix.org"
141→      "https://devenv.cachix.org"
142→      "https://nix-community.cachix.org"
143→    ];
144→    extra-trusted-public-keys = [
145→      "cache.nixos.org-1:REDACTED"
146→      "niks3.numtide.com-1:DTx8wZduET09hRmMtKdQDxNNthLQETkc/yaX7M4qK0g="
147→      "yuta.cachix.org-1:VGiC7m0kQjut7lp+RG/9pCRHFpzf11ELQrM2Nc2QCCk="
148→      "devenv.cachix.org-1:REDACTED/Hm9XgU50cw="
149→      "nix-community.cachix.org-1:REDACTED/rkCWyvRCYg3Fs="
150→    ];
151→  };
152→
153→  outputs =
154→    inputs@{
155→      flake-parts,
156→      treefmt-nix,
157→      nixpkgs,
158→      nixpkgs-stable,
159→      llm-agents,
160→      nix-steipete-tools,
161→      version-lsp,
162→      ghostty,
163→      gh-nippou,
164→      gh-graph,
165→      brew-nix,
166→      ...
167→    }:
168→    let
169→      mkPkgs =
170→        system:
171→        let
172→          isDarwin = builtins.match ".*-darwin" system != null;
173→        in
174→        import nixpkgs {
175→          inherit system;
176→          config = {
177→            allowUnfree = true;
178→            android_sdk.accept_license = true;
179→          };
180→          overlays = [
181→            (_final: _prev: {
182→              stable = import nixpkgs-stable {
183→                inherit system;
184→                config = {
185→                  allowUnfree = true;
186→                };
187→              };
188→            })
189→            (_final: _prev: {
190→              _llm-agents = llm-agents;
191→              _nix-steipete-tools = nix-steipete-tools;
192→              _version-lsp = version-lsp;
193→              _ghostty = ghostty;
194→              _repiq = inputs.repiq;
195→              _cage = inputs.cage;
196→            })
197→            gh-nippou.overlays.default
198→            gh-graph.overlays.default
199→            (import ./nix/overlays/default.nix)
200→          ]
201→          ++ nixpkgs.lib.optionals isDarwin [
202→            brew-nix.overlays.default
203→          ];
204→        };
205→    in
206→    flake-parts.lib.mkFlake { inherit inputs; } {
207→      systems = [
208→        "x86_64-linux"
209→        "aarch64-darwin"
210→      ];
211→
212→      imports = [
213→        treefmt-nix.flakeModule
214→        inputs.git-hooks.flakeModule
215→      ];
216→
217→      perSystem =
218→        {
219→          pkgs,
220→          system,
221→          config,
222→          ...
223→        }:
224→        let
225→          isDarwin = builtins.match ".*-darwin" system != null;
226→          darwinHostname = "M2-MacBook-Air";
227→          nom = "${pkgs.nix-output-monitor}/bin/nom";
228→          allPkgs = mkPkgs system;
229→        in
230→        {
231→          packages = {
232→            inherit (allPkgs)
233→              difit
234→              jj-desc
235→              keifu
236→              polycat
237→              pretty-ts-errors-markdown
238→              similarity-ts
239→              ;
240→          };
241→          apps = {
242→            build = {
243→              type = "app";
244→              program = toString (
245→                pkgs.writeShellScript "build" ''
246→                  set -e
247→                  ${
248→                    if isDarwin then
249→                      ''
250→                        echo "Building darwin configuration..."
251→                        ${nom} build ".#darwinConfigurations.${darwinHostname}.system"
252→                      ''
253→                    else
254→                      ''
255→                        HOSTNAME="$(hostname)"
256→                        echo "Building NixOS configuration for $HOSTNAME..."
257→                        ${nom} build ".#nixosConfigurations.$HOSTNAME.config.system.build.toplevel"
258→                      ''
259→                  }
260→                  echo "Build successful! Run 'nix run .#switch' to apply."
261→                ''
262→              );
263→            };
264→            switch = {
265→              type = "app";
266→              program = toString (
267→                pkgs.writeShellScript "switch" ''
268→                  set -eo pipefail
269→                  ${
270→                    if isDarwin then
271→                      ''
272→                        echo "Switching to darwin configuration..."
273→                        darwin-rebuild switch --flake ".#${darwinHostname}" |& ${nom}
274→                      ''
275→                    else
276→                      ''
277→                        HOSTNAME="$(hostname)"
278→                        echo "Switching to NixOS configuration for $HOSTNAME..."
279→                        sudo nixos-rebuild switch --flake ".#$HOSTNAME" |& ${nom}
280→                      ''
281→                  }
282→                  echo "Done!"
283→                ''
284→              );
285→            };
286→            fmt = {
287→              type = "app";
288→              program = toString (
289→                pkgs.writeShellScript "treefmt-wrapper" ''
290→                  exec ${config.treefmt.build.wrapper}/bin/treefmt "$@"
291→                ''
292→              );
293→            };
294→          };
295→
296→          treefmt = {
297→            projectRootFile = "flake.nix";
298→            programs = {
299→              nixfmt.enable = true;
300→              stylua.enable = true;
301→              shfmt.enable = true;
302→            };
303→            settings.global.excludes = [
304→              ".git/**"
305→              "*.lock"
306→            ];
307→          };
308→
309→          pre-commit = {
310→            check.enable = false;
311→            settings.hooks = {
312→              treefmt = {
313→                enable = true;
314→                package = config.treefmt.build.wrapper;
315→              };
316→              git-secrets = {
317→                enable = true;
318→                entry = "${pkgs.git-secrets}/bin/git-secrets --pre_commit_hook";
319→                language = "system";
320→                stages = [ "pre-commit" ];
321→                excludes = [ "^\.direnv/" ];
322→              };
323→            };
324→          };
325→
326→          devShells.default = pkgs.mkShell {
327→            shellHook = ''
328→              ${config.pre-commit.installationScript}
329→            '';
330→          };
331→        };
332→
333→      flake = {
334→        nixosConfigurations = {
335→          UM790-Pro = import ./nix/hosts/UM790-Pro { inherit inputs mkPkgs; };
336→          Still-Legend-x870 = import ./nix/hosts/Still-Legend-x870 { inherit inputs mkPkgs; };
337→        };
338→
339→        darwinConfigurations = {
340→          M2-MacBook-Air = import ./nix/hosts/M2-MacBook-Air { inherit inputs mkPkgs; };
341→        };
342→
343→        nixOnDroidConfigurations = {
344→          Galaxy-S23FE = import ./nix/hosts/Galaxy-S23FE { inherit inputs; };
345→        };
346→      };
347→    };
348→}
