let
  inherit (inputs)
    nixpkgs
    home-manager
    nix-hazkey
    nix-filter
    ;

  system = "x86_64-linux";

  helpers = import ../../modules/lib/helpers { lib = nixpkgs.lib; };
  dotfilesDir = "/home/yuta/ghq/github.com/yutakobayashidev/dotnix";

  local-skills = nix-filter.lib {
    root = inputs.self;
