121→
122→  nixConfig = {
123→    extra-substituters = [
124→      "https://cache.nixos.org"
125→      "https://cache.numtide.com"
126→      "https://yuta.cachix.org"
127→      "https://devenv.cachix.org"
128→      "https://nix-community.cachix.org"
129→    ];
130→    extra-trusted-public-keys = [
131→      "cache.nixos.org-1:REDACTED"
132→      "niks3.numtide.com-1:REDACTED"
133→      "yuta.cachix.org-1:REDACTED"
134→      "devenv.cachix.org-1:REDACTED"
135→      "nix-community.cachix.org-1:REDACTED"
136→    ];
137→  };
138→
139→  outputs =
140→    inputs@{
141→      flake-parts,
142→      treefmt-nix,
143→      nixpkgs,
144→      nixpkgs-stable,
145→      llm-agents,
146→      nix-steipete-tools,
147→      version-lsp,
148→      ghostty,
149→      gh-nippou,
150→      gh-graph,
151→      moonbit-overlay,
152→      brew-nix,
153→      ...
154→    }:
155→    let
156→      mkPkgs =
157→        system:
158→        let
159→          isDarwin = builtins.match ".*-darwin" system != null;
160→        in
161→        import nixpkgs {
162→          inherit system;
163→          config = {
164→            allowUnfree = true;
165→            android_sdk.accept_license = true;
166→          };
167→          overlays = [
168→            (_final: _prev: {
169→              stable = import nixpkgs-stable {
170→                inherit system;
171→                config = {
172→                  allowUnfree = true;
173→                };
174→              };
175→            })
176→            (_final: _prev: {
177→              _llm-agents = llm-agents;
178→              _nix-steipete-tools = nix-steipete-tools;
179→              _version-lsp = version-lsp;
180→              _ghostty = ghostty;
181→            })
182→            gh-nippou.overlays.default
183→            gh-graph.overlays.default
184→            (import ./nix/overlays/default.nix)
185→          ]
186→          ++ nixpkgs.lib.optionals (!isDarwin) [
187→            moonbit-overlay.overlays.default
188→          ]
189→          ++ nixpkgs.lib.optionals isDarwin [
190→            brew-nix.overlays.default
191→          ];
192→        };
193→    in
194→    flake-parts.lib.mkFlake { inherit inputs; } {
195→      systems = [
196→        "x86_64-linux"
197→        "aarch64-darwin"
198→      ];
199→
200→      imports = [
201→        treefmt-nix.flakeModule
202→        inputs.git-hooks.flakeModule
203→      ];
204→
205→      perSystem =
206→        {
207→          pkgs,
208→          system,
209→          config,
210→          ...
211→        }:
212→        let
213→          isDarwin = builtins.match ".*-darwin" system != null;
214→          darwinHostname = "M2-MacBook-Air";
215→          nom = "${pkgs.nix-output-monitor}/bin/nom";
216→          allPkgs = mkPkgs system;
217→        in
218→        {
219→          packages = {
220→            inherit (allPkgs)
221→              difit
222→              jj-desc
223→              keifu
224→              polycat
225→              pretty-ts-errors-markdown
226→              similarity-ts
227→              ;
228→          };
229→          apps = {
230→            build = {
231→              type = "app";
232→              program = toString (
233→                pkgs.writeShellScript "build" ''
234→                  set -e
235→                  ${
236→                    if isDarwin then
237→                      ''
238→                        echo "Building darwin configuration..."
239→                        ${nom} build ".#darwinConfigurations.${darwinHostname}.system"
240→                      ''
241→                    else
242→                      ''
243→                        HOSTNAME="$(hostname)"
244→                        echo "Building NixOS configuration for $HOSTNAME..."
245→                        ${nom} build ".#nixosConfigurations.$HOSTNAME.config.system.build.toplevel"
246→                      ''
247→                  }
248→                  echo "Build successful! Run 'nix run .#switch' to apply."
249→                ''
250→              );
251→            };
252→            switch = {
253→              type = "app";
254→              program = toString (
255→                pkgs.writeShellScript "switch" ''
256→                  set -eo pipefail
257→                  ${
258→                    if isDarwin then
259→                      ''
260→                        echo "Switching to darwin configuration..."
261→                        darwin-rebuild switch --flake ".#${darwinHostname}" |& ${nom}
262→                      ''
263→                    else
264→                      ''
265→                        HOSTNAME="$(hostname)"
266→                        echo "Switching to NixOS configuration for $HOSTNAME..."
267→                        sudo nixos-rebuild switch --flake ".#$HOSTNAME" |& ${nom}
268→                      ''
269→                  }
270→                  echo "Done!"
271→                ''
272→              );
273→            };
274→            fmt = {
275→              type = "app";
276→              program = toString (
277→                pkgs.writeShellScript "treefmt-wrapper" ''
278→                  exec ${config.treefmt.build.wrapper}/bin/treefmt "$@"
279→                ''
280→              );
281→            };
282→          };
283→
284→          treefmt = {
285→            projectRootFile = "flake.nix";
286→            programs = {
287→              nixfmt.enable = true;
288→              stylua.enable = true;
289→              shfmt.enable = true;
290→            };
291→            settings.global.excludes = [
292→              ".git/**"
293→              "*.lock"
294→            ];
295→          };
296→
297→          pre-commit = {
298→            check.enable = false;
299→            settings.hooks = {
300→              treefmt = {
301→                enable = true;
302→                package = config.treefmt.build.wrapper;
303→              };
304→              git-secrets = {
305→                enable = true;
306→                entry = "${pkgs.git-secrets}/bin/git-secrets --pre_commit_hook";
307→                language = "system";
308→                stages = [ "pre-commit" ];
309→                excludes = [ "^\.direnv/" ];
310→              };
311→            };
312→          };
313→
314→          devShells.default = pkgs.mkShell {
315→            shellHook = ''
316→              ${config.pre-commit.installationScript}
317→            '';
318→          };
319→        };
320→
321→      flake = {
322→        nixosConfigurations = {
323→          UM790-Pro = import ./nix/hosts/UM790-Pro { inherit inputs mkPkgs; };
324→          Still-Legend-x870 = import ./nix/hosts/Still-Legend-x870 { inherit inputs mkPkgs; };
325→        };
326→
327→        darwinConfigurations = {
328→          M2-MacBook-Air = import ./nix/hosts/M2-MacBook-Air { inherit inputs mkPkgs; };
329→        };
330→
331→        nixOnDroidConfigurations = {
332→          Galaxy-S23FE = import ./nix/hosts/Galaxy-S23FE { inherit inputs; };
333→        };
334→      };
335→    };
336→}
