 2‚Üí/*
 3‚Üí  social-digest.ts (CLI)
 4‚Üí
 5‚Üí  Fetch daily posts from Discord + Mastodon.
 6‚Üí  Outputs JSON to stdout.
 7‚Üí
 8‚Üí  Env:
 9‚Üí    DISCORD_BOT_TOKEN
10‚Üí    MASTODON_TOKEN
11‚Üí    MASTODON_BASE_URL (optional)
12‚Üí*/
13‚Üí
14‚Üíimport { parseArgs, jstDateRange, stripHtml, parseMastodonAcct, extractUrls } from "../src/socialDigest";
15‚Üí
16‚Üítype DiscordMessage = any;
17‚Üí
18‚Üítype Output = {
19‚Üí  ok: true;
20‚Üí  ymd: string;
21‚Üí  range: { start: string; end: string };
22‚Üí  discord: null | { channelId: string; messages: Array<{ id: string; timestamp: string; author: any; content: string; url: string; links: string[] }> };
23‚Üí  mastodon: null | { acct: string; baseUrl: string; statuses: Array<{ id: string; created_at: string; url: string; content_text: string; links: string[] }> };
24‚Üí};
25‚Üí
26‚Üífunction sleep(ms: number) {
27‚Üí  return new Promise((r) => setTimeout(r, ms));
28‚Üí}
29‚Üí
30‚Üíasync function discordFetchMessagesPaged(
31‚Üí  channelId: string,
32‚Üí  opts: { start: Date; end: Date; pageLimit?: number; maxPages?: number }
33‚Üí): Promise<DiscordMessage[]> {
34‚Üí  const token = process.env.DISCORD_BOT_TOKEN;
35‚Üí  if (!token) throw new Error("DISCORD_BOT_TOKEN is required");
36‚Üí
37‚Üí  const pageLimit = Math.max(1, Math.min(100, opts.pageLimit ?? 100));
38‚Üí  const maxPages = Math.max(1, Math.min(5000, opts.maxPages ?? 1000));
39‚Üí
40‚Üí  const out: DiscordMessage[] = [];
41‚Üí  let before: string | undefined;
42‚Üí
43‚Üí  // Discord returns newest-first. We paginate backwards using `before` until the oldest fetched message
44‚Üí  // is older than start (meaning we've fully covered the day window).
45‚Üí  for (let pageNo = 0; pageNo < maxPages; pageNo++) {
46‚Üí    const url = new URL(`https://discord.com/api/v10/channels/${channelId}/messages`);
47‚Üí    url.searchParams.set("limit", String(pageLimit));
48‚Üí    if (before) url.searchParams.set("before", before);
49‚Üí
50‚Üí    let res = await fetch(url.toString(), {
51‚Üí      headers: {
52‚Üí        Authorization: `Bot ${token}`,
53‚Üí        "User-Agent": "openclaw-social-digest (https://openclaw.ai)",
54‚Üí      },
55‚Üí    });
56‚Üí
57‚Üí    // Respect rate limits.
58‚Üí    if (res.status === 429) {
59‚Üí      const data = await res.json().catch(() => null as any);
60‚Üí      const retryAfterMs = Math.ceil(((data?.retry_after ?? 1) as number) * 1000);
61‚Üí      await sleep(Math.min(60000, Math.max(250, retryAfterMs)));
62‚Üí      res = await fetch(url.toString(), {
63‚Üí        headers: {
64‚Üí          Authorization: `Bot ${token}`,
65‚Üí          "User-Agent": "openclaw-social-digest (https://openclaw.ai)",
66‚Üí        },
67‚Üí      });
68‚Üí    }
69‚Üí
70‚Üí    if (!res.ok) {
71‚Üí      const t = await res.text().catch(() => "");
72‚Üí      throw new Error(`Discord API error ${res.status}: ${t}`);
73‚Üí    }
74‚Üí
75‚Üí    const page: DiscordMessage[] = await res.json();
76‚Üí    if (!page.length) break;
77‚Üí
78‚Üí    out.push(...page);
79‚Üí    before = page[page.length - 1]?.id;
