import { DEFAULT_HOST, DEFAULT_PORT } from "@/models/constants";
import { getLogger } from "@/infrastructure/logging/pino-logger";
import { serve } from "@hono/node-server";
import type { AddressInfo } from "node:net";
import { config } from "../env";
import app, { injectWebSocket } from "./routes/v1";

config();

const logger = getLogger("server");

const port = Number(process.env.PORT) || DEFAULT_PORT;
const host = process.env.HOST || DEFAULT_HOST;

const server = serve(
  {
    fetch: (req) => app.fetch(req),
    port,
    hostname: host,
  },
  (info) => {
    if (typeof info === "string") {
      logger.info({ address: info }, "Server listening");
      return;
    }

    const { port, address } = info as AddressInfo;
    logger.info({ address, port }, "Server listening");
  },
);

injectWebSocket(server);

