        pkgs = mkPkgs "aarch64-darwin";
        inherit (nixpkgs) lib;
        inherit username;
        homedir = darwinHomedir;
      })
      nix-index-database.darwinModules.nix-index
      home-manager.darwinModules.home-manager
      {
        home-manager = {
          useGlobalPkgs = false;
          useUserPackages = true;
          extraSpecialArgs = { pkgs = mkPkgs "aarch64-darwin"; };
          users.${username} = { pkgs, config, lib, ... }: {
            imports = [
              agent-skills.homeManagerModules.default
              (import ./nix/modules/home { /* ... */ })
              (import ./nix/modules/darwin { /* ... */ })
            ];
          };
        };
      }
    ];
  };

  # Linux: standalone home-managerで管理
  homeConfigurations = {
    ${username} = mkLinuxHomeConfig "x86_64-linux";
    "${username}-aarch64" = mkLinuxHomeConfig "aarch64-linux";
  };
};
```

**あなたの flake.nix との違い**: あなたは `nixosConfigurations` (NixOSシステム全体) を使っているのに対し、ryoppippi は Linux側は `homeConfigurations` (standalone home-manager) のみ。NixOSを使っている場合は `nixosConfigurations` を `flake` ブロックに入れる形になります。

---

### 3. treefmt-nix の perSystem 統合

`treefmt-nix.flakeModule` を `imports` に入れると、`perSystem` 内で `treefmt` オプションが使えるようになります。

```nix
imports = [
  treefmt-nix.flakeModule  # これでperSystem.treefmtが有効になる
];

perSystem = { config, system, ... }:
  let
    localPkgs = mkPkgs system;
  in {
    treefmt = {
      projectRootFile = "flake.nix";  # プロジェクトルートの判定ファイル

      # ビルトインフォーマッター
      programs = {
        nixfmt = {
          enable = true;
          package = localPkgs.nixfmt-rfc-style;
        };
        stylua.enable = true;
        shfmt.enable = true;
      };

      # カスタムフォーマッター・チェッカー
      settings = {
        global.excludes = [ ".git/**" "*.lock" ];
        formatter = {
          # カスタムフォーマッター: oxfmt
          oxfmt = {
            command = "${localPkgs.oxfmt}/bin/oxfmt";
            options = [ "--no-error-on-unmatched-pattern" ];
            includes = [ "*" ];
            excludes = [ "nvim/template/**" "nvim/lazy-lock.json" ];
          };
          # セキュリティスキャン: gitleaks
          gitleaks = {
            command = "${localPkgs.gitleaks}/bin/gitleaks";
            options = [ "detect" "--no-git" "--exit-code" "0" ];
            includes = [ "*" ];
            excludes = [ "*.png" "*.jpg" /* ... */ ];
          };
          # Renovate設定バリデーション
          renovate-validator = {
            command = "${localPkgs.renovate}/bin/renovate-config-validator";
            options = [ "--strict" ];
            includes = [ ".github/renovate.json5" ];
          };
          # fish_indent
          fish-indent = {
            command = "${localPkgs.fish}/bin/fish_indent";
            options = [ "--write" ];
            includes = [ "*.fish" ];
          };
        };
      };
    };
  };
```

**flakeModule が自動で行うこと**:
- `formatter.<system>` に `config.treefmt.build.wrapper` を設定 → `nix fmt` で実行可能
- `checks.<system>.treefmt` に `config.treefmt.build.check` を設定 → `nix flake check` で検証可能
- これらは `flakeFormatter` (デフォルト `true`) と `flakeCheck` (デフォルト `true`) で制御

---

### 4. perSystem での apps 定義

`perSystem` 内の `apps` は **システムごとに自動展開**されます。`isDarwin` で条件分岐してプラットフォーム固有のコマンドを生成しています。

```nix
perSystem = { config, system, ... }:
  let
    localPkgs = mkPkgs system;
    inherit (localPkgs.stdenv) isDarwin;
    homedir = if isDarwin then darwinHomedir else linuxHomedir;
    hostname = username;
  in {
    apps = {
      # ★ build: ビルドのみ（適用なし）
      build = {
        type = "app";
        program = toString (
          localPkgs.writeShellScript (if isDarwin then "darwin-build" else "home-manager-build") ''
            set -e
            echo "Building ${if isDarwin then "darwin" else "Home Manager"} configuration..."
            ${localPkgs.nix-output-monitor}/bin/nom build .#${
              if isDarwin then
                "darwinConfigurations.${hostname}.system"
              else
                "homeConfigurations.${username}.activationPackage"
            }
            echo "Build successful! Run 'nix run .#switch' to apply."
          ''
        );
      };

      # ★ switch: ビルド＋適用
      switch = {
        type = "app";
        program = toString (
          localPkgs.writeShellScript (if isDarwin then "darwin-switch" else "home-manager-switch") ''
            set -eo pipefail
            echo "Building and switching to ${if isDarwin then "darwin" else "Home Manager"} configuration..."
            ${
              if isDarwin then
                "sudo nix run nix-darwin -- switch --flake .#${hostname} |& ${localPkgs.nix-output-monitor}/bin/nom"
              else
                "nix run nixpkgs#home-manager -- switch --flake .#${username} |& ${localPkgs.nix-output-monitor}/bin/nom"
            }
            echo "Clearing fish cache..."
            rm -rf "$TMPDIR/fish-cache"
            echo "Done!"
          ''
        );
      };

      # ★ update: flake入力の更新
      update = {
        type = "app";
        program = toString (
          localPkgs.writeShellScript "flake-update" ''
            set -e
            echo "Updating flake.lock..."
            nix flake update
            echo "Done! Run 'nix run .#switch' to apply changes."
          ''
        );
      };

      # ★ fmt: treefmt wrapper
      fmt = {
        type = "app";
        program = toString (
          localPkgs.writeShellScript "treefmt-wrapper" ''
            exec ${config.treefmt.build.wrapper}/bin/treefmt "$@"
          ''
        );
      };

      # ★ update-ai-tools: 特定入力のみ更新
      update-ai-tools = {
        type = "app";
        program = toString (
          localPkgs.writeShellScript "update-ai-tools" ''
            set -e
            echo "Updating AI tools inputs..."
            nix flake update llm-agents
            echo "Done! Run 'nix run .#switch' to apply changes."
          ''
        );
      };
    };

    # ★ devShell: pre-commit hooks統合
    devShells.default = localPkgs.mkShell {
      shellHook = ''
        ${config.pre-commit.installationScript}
      '';
    };
  };
```

**重要なパターン**: `config.treefmt.build.wrapper` を `fmt` app 内で参照して treefmt の設定済みラッパーを呼び出しています。これは `perSystem` 内の `config` で同一 perSystem 内の他のオプション（treefmt）を参照できるためです。

---

### 5. treefmt-nix の使い方まとめ（flake-parts あり/なし）

#### flake-parts ありの場合（推奨）

```nix
{
  inputs = {
    flake-parts.url = "github:hercules-ci/flake-parts";
    treefmt-nix.url = "github:numtide/treefmt-nix";
  };

  outputs = { flake-parts, treefmt-nix, ... }:
    flake-parts.lib.mkFlake { inherit inputs; } {
      imports = [ treefmt-nix.flakeModule ];
      systems = [ "x86_64-linux" "aarch64-darwin" ];
      perSystem = { pkgs, config, ... }: {
        treefmt = {
          projectRootFile = "flake.nix";
          programs.nixfmt.enable = true;
        };
        # config.treefmt.build.wrapper で treefmt実行ファイルにアクセス
        # formatter と checks は自動設定される
      };
    };
}
```

#### flake-parts なしの場合

```nix
{
  inputs.treefmt-nix.url = "github:numtide/treefmt-nix";

  outputs = { self, nixpkgs, treefmt-nix, ... }:
    let
      system = "x86_64-linux";
      pkgs = nixpkgs.legacyPackages.${system};
      treefmtEval = treefmt-nix.lib.evalModule pkgs {
        projectRootFile = "flake.nix";
        programs.nixfmt.enable = true;
      };
    in {
      formatter.${system} = treefmtEval.config.build.wrapper;
      checks.${system}.formatting = treefmtEval.config.build.check self;
    };
}
```

---

### 6. あなたの flake.nix との比較・移行時のポイント

| 観点 | 現在の dotnix | ryoppippi/dotfiles |
|---|---|---|
| flake構造 | 素の `outputs` attrset | `flake-parts.lib.mkFlake` |
| apps定義 | `apps.<system>` を `mkApps` で手動展開 | `perSystem.apps` で自動展開 |
| formatter | なし | `perSystem.treefmt` (flakeModule) |
| pre-commit | なし | `perSystem.pre-commit` (git-hooks.flakeModule) |
| NixOS設定 | `nixosConfigurations` (直接) | `flake.darwinConfigurations` / `flake.homeConfigurations` |
| システム一覧 | 手動で `x86_64-linux`, `aarch64-darwin` | `systems = [...]` で宣言 |

もし flake-parts へ移行する場合、あなたの `nixosConfigurations` と `darwinConfigurations` は `flake` ブロックにそのまま移動し、`apps` の `mkApps` パターンは `perSystem.apps` に置き換え、treefmt と git-hooks を `imports` + `perSystem` で追加する形になります。

