        path = inputs.hashicorp-agent-skills;
        subdir = ".";
      };
      # Deno: skills from denoland/skills
      deno = {
        path = inputs.deno-skills;
        subdir = "skills";
      };
      # AWS: skills from itsmostafa/aws-agent-skills
      aws = {
        path = inputs.aws-agent-skills;
        subdir = "skills";
      };
    };

    # Enable all local skills
    skills.enableAll = [
      "local"
      "cloudflare"
      "hashicorp"
      "deno"
      "aws"
    ];

    # Enable specific Anthropic skills
    skills.explicit = {
      # Document skills
      docx = {
        from = "anthropic";
        path = "docx";
      };
      pdf = {
        from = "anthropic";
        path = "pdf";
      };
      pptx = {
        from = "anthropic";
        path = "pptx";
      };
      xlsx = {
        from = "anthropic";
        path = "xlsx";
      };
      # Example skills
      frontend-design = {
        from = "anthropic";
        path = "frontend-design";
      };
      skill-creator = {
        from = "anthropic";
        path = "skill-creator";
      };
      webapp-testing = {
        from = "anthropic";
        path = "webapp-testing";
      };
      # Vercel skills
      find-skills = {
        from = "vercel";
        path = "find-skills";
      };
      # nextlevelbuilder skills
      ui-ux-pro-max = {
        from = "nextlevelbuilder";
        path = "ui-ux-pro-max";
      };
      # ast-grep skills
      ast-grep = {
        from = "ast-grep";
        path = "ast-grep";
      };
    };

    # Deploy to standard skills directories
    targets = {
      # Standard ~/.agents/skills directory
      agents = {
        dest = ".agents/skills";
        structure = "link";
      };
      # Claude Code user config
      claude = {
        dest = ".config/claude/skills";
        structure = "link";
      };
      # Codex CLI user config (XDG: ~/.config/codex/skills)
      codex = {
        dest = ".config/codex/skills";
        structure = "link";
      };
    };
  };
}

