292→                  ${nom} build ${
293→                    if isDarwin then
294→                      ".#darwinConfigurations.${hostname}.system"
295→                    else
296→                      ".#nixosConfigurations.${hostname}.config.system.build.toplevel"
297→                  }
298→                  echo "Build successful! Run 'nix run .#switch' to apply."
299→                ''
300→              );
301→            };
302→            switch = {
303→              type = "app";
304→              program = toString (
305→                pkgs.writeShellScript "switch" ''
306→                  set -eo pipefail
307→                  echo "Switching to ${if isDarwin then "darwin" else "NixOS"} configuration..."
308→                  ${
309→                    if isDarwin then
310→                      "darwin-rebuild switch --flake .#${hostname} |& ${nom}"
311→                    else
312→                      "sudo nixos-rebuild switch --flake .#${hostname} |& ${nom}"
313→                  }
314→                  echo "Done!"
315→                ''
316→              );
317→            };
318→            fmt = {
319→              type = "app";
320→              program = toString (
321→                pkgs.writeShellScript "treefmt-wrapper" ''
322→                  exec ${config.treefmt.build.wrapper}/bin/treefmt "$@"
323→                ''
324→              );
325→            };
326→          };
327→
328→          treefmt = {
329→            projectRootFile = "flake.nix";
330→            programs = {
331→              nixfmt.enable = true;
332→              stylua.enable = true;
333→              shfmt.enable = true;
334→            };
335→            settings.global.excludes = [
336→              ".git/**"
337→              "*.lock"
338→            ];
339→          };
340→
341→          pre-commit = {
342→            check.enable = false;
343→            settings.hooks = {
344→              treefmt = {
345→                enable = true;
346→                package = config.treefmt.build.wrapper;
347→              };
348→              git-secrets = {
349→                enable = true;
350→                entry = "${pkgs.git-secrets}/bin/git-secrets --pre_commit_hook";
351→                language = "system";
352→                stages = [ "pre-commit" ];
353→                excludes = [ "^\.direnv/" ];
354→              };
355→            };
356→          };
357→
358→          devShells.default = pkgs.mkShell {
359→            shellHook = ''
360→              ${config.pre-commit.installationScript}
361→            '';
362→          };
363→        };
364→
365→      flake = {
366→        nixosConfigurations = {
367→          nixos = mkSystem {
368→            host = "nixos";
369→            system = "x86_64-linux";
370→            profile = "gui";
371→            extraModules = [ ];
372→          };
373→        };
374→
375→        darwinConfigurations = {
376→          darwin = mkDarwin {
377→            host = "darwin";
378→            system = "aarch64-darwin";
379→          };
380→        };
381→      };
382→    };
383→}
